export default async function HombresPage() {
  const supabase = getSupabasePublicClient();

  const { data } = await supabase
    .from("publicaciones")
    .select(
      "id, nombre, edad, zona, ciudad, cover_url, disponible, rating, created_at, user_id, categoria",
    )
    .eq("categoria", "hombre")
    .not("cover_url", "is", null)
    .neq("cover_url", "");

  const items = (data || []) as Publicacion[];

  return (
    <main>
      <div className="vv-page-header">
        <h1 className="vv-page-title">Hombres</h1>
        <p className="vv-page-subtitle">
          {items.length} perfil{items.length !== 1 ? "es" : ""}
        </p>
      </div>

      <div className="vv-grid">
        {items.map((item) => (
          <VVCard key={item.id} item={item} basePath="/hombres" />
        ))}
      </div>
    </main>
  );
}

