export default async function TransPage() {
  const supabase = getSupabasePublicClient();
  if (!supabase) {
    return (
      <main>
        <div className="vv-page-header">
          <h1 className="vv-page-title">Trans</h1>
        </div>
        <div className="vv-empty">Error de configuracin.</div>
      </main>
    );
  }

  let items: Publicacion[] = [];
  let errorMsg: string | null = null;

  try {
    const { data: pubs, error: pubError } = await supabase
      .from("publicaciones")
      .select(
        "id, nombre, edad, zona, ciudad, cover_url, disponible, rating, created_at, user_id, categoria",
      )
      .eq("categoria", "trans")
      .not("cover_url", "is", null)
      .neq("cover_url", "");

    if (pubError) {
      errorMsg = pubError.message;
    } else {
      const pubList = (pubs || []) as Publicacion[];
      const userIds = pubList.map((p) => p.user_id).filter(Boolean) as string[];

      let planMap: Record<string, string> = {};
      if (userIds.length > 0) {
        const { data: profiles } = await supabase
          .from("profiles")
          .select("id, plan_actual, plan_estado")
          .in("id", userIds);

        if (profiles) {
          for (const p of profiles as any[]) {
            planMap[p.id] =
              p.plan_estado === "activo" ? p.plan_actual || "free" : "free";
          }
        }
      }

      items = pubList.map((p) => ({
        ...p,
        plan_actual: p.user_id ? planMap[p.user_id] || "free" : "free",
      }));

      // Orden tipo Limbo: plan > disponible > rating > newest
      items.sort((a, b) => {
        const pa = getPlanConfig(a.plan_actual || "free").ranking_priority;
        const pb = getPlanConfig(b.plan_actual || "free").ranking_priority;
        if (pb !== pa) return pb - pa;

        const da = a.disponible !== false ? 1 : 0;
        const db = b.disponible !== false ? 1 : 0;
        if (db !== da) return db - da;

        const ra = a.rating ?? 0;
        const rb = b.rating ?? 0;
        if (rb !== ra) return rb - ra;

        const ta = a.created_at ? new Date(a.created_at).getTime() : 0;
        const tb = b.created_at ? new Date(b.created_at).getTime() : 0;
        return tb - ta;
      });
    }
  } catch (err: unknown) {
    errorMsg = err instanceof Error ? err.message : "Error desconocido";
  }

  if (errorMsg) {
    return (
      <main>
        <div className="vv-page-header">
          <h1 className="vv-page-title">Trans</h1>
        </div>
        <div className="vv-empty">Error: {errorMsg}</div>
      </main>
    );
  }

  if (items.length === 0) {
    return (
      <main>
        <div className="vv-page-header">
          <h1 className="vv-page-title">Trans</h1>
        </div>
        <div className="vv-empty">No hay publicaciones an.</div>
      </main>
    );
  }

  return (
    <main>
      <div className="vv-page-header">
        <h1 className="vv-page-title">Trans</h1>
        <p className="vv-page-subtitle">
          {items.length} perfil{items.length !== 1 ? "es" : ""}
        </p>
      </div>

      <div className="vv-grid">
        {items.map((item) => (
          <VVCard key={item.id} item={item} basePath="/trans" />
        ))}
      </div>
    </main>
  );
}

