app/metricas/page.tsx.content:
"use client";

import { useEffect, useState } from "react";
import { getSupabase } from "@/lib/supabaseClient";
import { getPlanConfig } from "@/lib/plans";

interface MetricRow {
  fecha: string;
  visitas: number;
  clicks_whatsapp: number;
  favoritos: number;
}

export default function MetricasPage() {
  const [loading, setLoading] = useState(true);
  const [authError, setAuthError] = useState<string | null>(null);
  const [metrics, setMetrics] = useState<MetricRow[]>([]);
  const [planId, setPlanId] = useState<string>("free");

  useEffect(() => {
    async function load() {
      const supabase = getSupabase();
      if (!supabase) { setAuthError("Supabase no configurado."); setLoading(false); return; }

      const { data } = await supabase.auth.getUser();
      const user = data?.user;
      if (!user) { setAuthError("Inicia sesion."); setLoading(false); return; }

      const { data: profile } = await supabase.from("profiles").select("plan_actual, plan_estado").eq("id", user.id).maybeSingle();
      const plan = profile?.plan_actual || "free";
      setPlanId(plan);
      const config = getPlanConfig(plan);

      if (!config.metricas) {
        setAuthError("Tu plan no incluye metricas. Mejora a Platino o Diamante.");
        setLoading(false);
        return;
      }

      if (profile?.plan_estado !== "activo") {
        setAuthError("Tu plan esta vencido. Renova para acceder a metricas.");
        setLoading(false);
        return;
      }

      const { data: rows, error } = await supabase.from("profile_metrics").select("fecha, visitas, clicks_whatsapp, favoritos").eq("profile_id", user.id).order("fecha", { ascending: false }).limit(30);
      if (error) { setAuthError(`Error: ${error.message}`); } else { setMetrics((rows || []) as MetricRow[]); }
      setLoading(false);
    }
    load();
  }, []);

  if (loading) return <main className="vv-form-page"><div className="vv-form-container"><p className="vv-form-loading">Cargando...</p></div></main>;

  if (authError) {
    return (
      <main className="vv-form-page">
        <div className="vv-form-container">
          <div className="vv-form-header"><h1 className="vv-form-title">Estadisticas</h1></div>
          <div className="vv-form-error-box">{authError}</div>
          <a href="/planes" className="vv-form-link">Ver planes</a>
        </div>
      </main>
    );
  }

  const config = getPlanConfig(planId);
  const today = new Date().toISOString().split("T")[0];
  const todayMetric = metrics.find((m) => m.fecha === today);
  const last7 = metrics.slice(0, 7);
  const totalVisitas7 = last7.reduce((s, m) => s + (m.visitas || 0), 0);
  const totalClicks7 = last7.reduce((s, m) => s + (m.clicks_whatsapp || 0), 0);
  const totalFav7 = last7.reduce((s, m) => s + (m.favoritos || 0), 0);

  const prev7 = metrics.slice(7, 14);
  const prevVisitas7 = prev7.reduce((s, m) => s + (m.visitas || 0), 0);
  const tendencia = prevVisitas7 > 0 ? Math.round(((totalVisitas7 - prevVisitas7) / prevVisitas7) * 100) : totalVisitas7 > 0 ? 100 : 0;

  return (
    <main className="vv-form-page">
      <div className="vv-form-container">
        <div className="vv-form-header">
          <h1 className="vv-form-title">Estadisticas</h1>
          <p className="vv-form-subtitle">Interes en tu perfil</p>
        </div>

        <div className="vv-metrics-grid">
          <div className="vv-metric-card">
            <span className="vv-metric-label">Visitas hoy</span>
            <span className="vv-metric-value">{todayMetric?.visitas || 0}</span>
          </div>
          <div className="vv-metric-card">
            <span className="vv-metric-label">Visitas 7 dias</span>
            <span className="vv-metric-value">{totalVisitas7}</span>
          </div>
          <div className="vv-metric-card">
            <span className="vv-metric-label">Clicks WhatsApp</span>
            <span className="vv-metric-value">{totalClicks7}</span>
          </div>
          {config.metricas_avanzadas && (
            <>
              <div className="vv-metric-card">
                <span className="vv-metric-label">Favoritos 7d</span>
                <span className="vv-metric-value">{totalFav7}</span>
              </div>
              <div className="vv-metric-card">
                <span className="vv-metric-label">Tendencia semanal</span>
                <span className={`vv-metric-value ${tendencia >= 0 ? "vv-metric-up" : "vv-metric-down"}`}>
                  {tendencia >= 0 ? "+" : ""}{tendencia}%
                </span>
              </div>
            </>
          )}
        </div>

        {config.metricas_avanzadas && metrics.length > 0 && (
          <div className="vv-metrics-table-wrap">
            <h2 className="vv-cuenta-label" style={{ marginTop: 24 }}>Historial</h2>
            <table className="vv-metrics-table">
              <thead>
                <tr><th>Fecha</th><th>Visitas</th><th>Clicks</th><th>Favs</th></tr>
              </thead>
              <tbody>
                {metrics.slice(0, 14).map((m) => (
                  <tr key={m.fecha}><td>{m.fecha}</td><td>{m.visitas}</td><td>{m.clicks_whatsapp}</td><td>{m.favoritos}</td></tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        <a href="/mi-cuenta" className="vv-form-back" style={{ display: "block", marginTop: 24 }}>Volver a Mi Cuenta</a>
      </div>
    </main>
  );
}

