app/components/Header.tsx.content:
"use client";

import { useEffect, useState } from "react";
import { useRouter, usePathname } from "next/navigation";
import { getSupabase } from "@/lib/supabaseClient";

const DEPARTAMENTOS = [
  "Todos", "Montevideo", "Maldonado", "Canelones", "Colonia", "Salto",
  "Paysandu", "Rivera", "Cerro Largo", "Rocha", "Soriano",
];

const MENU_ITEMS = [
  { label: "Mujeres", href: "/mujeres", icon: "woman" },
  { label: "Trans", href: "/trans", icon: "trans" },
  { label: "Hombres", href: "/hombres", icon: "man" },
  { label: "Disponibles ahora", href: "/mujeres", icon: "clock" },
  { label: "Nuevas", href: "/mujeres", icon: "sparkle" },
  { label: "Virtual", href: "/mujeres", icon: "video" },
  { label: "Cerca de mi", href: "/mujeres", icon: "pin" },
  { label: "Publicar", href: "/publicar", icon: "plus" },
  { label: "Mi cuenta", href: "/mi-cuenta", icon: "user" },
];

function MenuIcon({ name }: { name: string }) {
  const s = { width: 18, height: 18, fill: "none", stroke: "currentColor", strokeWidth: 1.5 };
  switch (name) {
    case "woman":
      return <svg viewBox="0 0 24 24" style={s}><circle cx="12" cy="8" r="5" /><path d="M12 13v8M9 18h6" strokeLinecap="round" /></svg>;
    case "trans":
      return <svg viewBox="0 0 24 24" style={s}><circle cx="12" cy="12" r="6" /><path d="M12 6V2m4 2l-4 4-4-4" strokeLinecap="round" strokeLinejoin="round" /></svg>;
    case "man":
      return <svg viewBox="0 0 24 24" style={s}><circle cx="10" cy="14" r="5" /><path d="M21 3l-6.5 6.5M21 3h-5M21 3v5" strokeLinecap="round" strokeLinejoin="round" /></svg>;
    case "clock":
      return <svg viewBox="0 0 24 24" style={s}><circle cx="12" cy="12" r="9" /><path d="M12 7v5l3 3" strokeLinecap="round" /></svg>;
    case "sparkle":
      return <svg viewBox="0 0 24 24" style={s}><path d="M12 2l2.4 7.2L22 12l-7.6 2.8L12 22l-2.4-7.2L2 12l7.6-2.8L12 2z" strokeLinejoin="round" /></svg>;
    case "video":
      return <svg viewBox="0 0 24 24" style={s}><rect x="2" y="6" width="13" height="12" rx="2" /><path d="M15 12l7-4v8l-7-4z" /></svg>;
    case "pin":
      return <svg viewBox="0 0 24 24" style={s}><path d="M12 21s-7-5.3-7-10a7 7 0 1114 0c0 4.7-7 10-7 10z" /><circle cx="12" cy="11" r="2" /></svg>;
    case "plus":
      return <svg viewBox="0 0 24 24" style={s}><path d="M12 5v14M5 12h14" strokeLinecap="round" /></svg>;
    case "user":
      return <svg viewBox="0 0 24 24" style={s}><circle cx="12" cy="8" r="4" /><path d="M20 21c0-3.3-3.6-6-8-6s-8 2.7-8 6" /></svg>;
    default:
      return null;
  }
}

export default function Header() {
  const router = useRouter();
  const pathname = usePathname();
  const [loggedIn, setLoggedIn] = useState(false);
  const [checking, setChecking] = useState(true);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    async function check() {
      const supabase = getSupabase();
      if (!supabase) { setChecking(false); return; }
      const { data } = await supabase.auth.getUser();
      setLoggedIn(!!data?.user);
      setChecking(false);
    }
    check();
  }, []);

  useEffect(() => {
    setMenuOpen(false);
  }, [pathname]);

  return (
    <>
      <header className="vv-header">
        <div className="vv-header-main">
          <div className="vv-header-left">
            <button
              className="vv-hamburger"
              onClick={() => setMenuOpen(true)}
              data-testid="button-hamburger"
            >
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round">
                <path d="M3 6h18M3 12h18M3 18h18" />
              </svg>
            </button>
          </div>

          <a href="/" className="vv-logo" data-testid="link-logo">VIAVIP</a>

          <div className="vv-header-right">
            <button
              className="vv-header-icon-btn"
              onClick={() => router.push("/favoritos")}
              data-testid="button-favoritos-header"
              aria-label="Favoritos"
            >
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5">
                <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
              </svg>
            </button>
            {!checking && (
              loggedIn ? (
                <button
                  className="vv-header-icon-btn"
                  onClick={() => router.push("/mi-cuenta")}
                  data-testid="button-account"
                >
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5">
                    <circle cx="12" cy="8" r="4" />
                    <path d="M20 21c0-3.3-3.6-6-8-6s-8 2.7-8 6" />
                  </svg>
                </button>
              ) : (
                <button
                  className="vv-header-icon-btn"
                  onClick={() => router.push("/registro")}
                  data-testid="button-register"
                >
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5">
                    <circle cx="12" cy="8" r="4" />
                    <path d="M20 21c0-3.3-3.6-6-8-6s-8 2.7-8 6" />
                    <path d="M20 8v4m2-2h-4" strokeLinecap="round" />
                  </svg>
                </button>
              )
            )}
          </div>
        </div>

        <div className="vv-location-bar">
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" style={{ width: 14, height: 14, flexShrink: 0 }}>
            <path d="M12 21s-7-5.3-7-10a7 7 0 1114 0c0 4.7-7 10-7 10z" />
            <circle cx="12" cy="11" r="2" />
          </svg>
          <span>Uruguay</span>
          <select className="vv-location-select" data-testid="select-departamento" defaultValue="Todos">
            {DEPARTAMENTOS.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>
      </header>

      <div
        className={`vv-overlay ${menuOpen ? "vv-overlay-open" : ""}`}
        onClick={() => setMenuOpen(false)}
      />
      <nav className={`vv-side-menu ${menuOpen ? "vv-side-menu-open" : ""}`}>
        <div className="vv-side-menu-header">
          <span className="vv-side-menu-logo">VIAVIP</span>
          <button
            className="vv-side-menu-close"
            onClick={() => setMenuOpen(false)}
            data-testid="button-close-menu"
          >
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round">
              <path d="M18 6L6 18M6 6l12 12" />
            </svg>
          </button>
        </div>
        <ul className="vv-side-menu-list">
          {MENU_ITEMS.map((item) => (
            <li key={item.label} className="vv-side-menu-item">
              <a href={item.href} className="vv-side-menu-link" data-testid={`link-menu-${item.label.toLowerCase().replace(/ /g, '-')}`}>
                <MenuIcon name={item.icon} />
                {item.label}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </>
  );
}

