import Link from "next/link";
import { getSupabasePublicClient } from "@/lib/supabasePublic";
import { getPlanConfig } from "@/lib/plans";

interface Publicacion {
  id: string | number;
  nombre?: string;
  edad?: number;
  zona?: string;
  ciudad?: string;
  cover_url?: string;
  disponible?: boolean;
  rating?: number;
  created_at?: string;
  user_id?: string;
  plan_actual?: string;
  categoria?: "mujer" | "hombre" | "trans";
}

function HeartIcon() {
  return (
    <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
      <path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z" />
    </svg>
  );
}

function StarIcon() {
  return (
    <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
      <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
    </svg>
  );
}

function PlanBadge({ planId }: { planId: string }) {
  if (!planId || planId === "free") return null;
  const config = getPlanConfig(planId);
  return (
    <div
      className="vv-card-plan-badge"
      style={{ background: config.badge_bg, color: config.badge_text }}
    >
      {config.name}
    </div>
  );
}

function VVCard({ item, basePath }: { item: Publicacion; basePath: string }) {
  const nombre = item.nombre || "Sin nombre";
  const edad = item.edad || null;
  const zona = item.zona || item.ciudad || "";
  const disponible = item.disponible !== undefined ? item.disponible : true;
  const rating = item.rating != null ? item.rating : 4.8;

  return (
    <Link
      href={`${basePath}/${item.id}`}
      className="vv-card"
      data-testid={`card-${item.id}`}
    >
      <div className="vv-card-img-wrap">
        {item.cover_url ? (
          <img
            src={item.cover_url}
            alt={nombre}
            className="vv-card-img"
            loading="lazy"
          />
        ) : (
          <div className="vv-card-img-placeholder" />
        )}

        {disponible && (
          <div className="vv-card-badge vv-card-badge-disponible">
            Disponible
          </div>
        )}

        <PlanBadge planId={item.plan_actual || ""} />

        <div className="vv-card-heart">
          <HeartIcon />
        </div>

        <div className="vv-card-overlay">
          <p className="vv-card-name">
            {nombre}
            {edad ? `, ${edad}` : ""}
          </p>
          {zona && <p className="vv-card-detail">{zona}</p>}
        </div>

        <div className="vv-card-rating">
          <StarIcon />
          {rating.toFixed(1)}
        </div>
      </div>
    </Link>
  );
}

export default async function MujeresPage() {
  const supabase = getSupabasePublicClient();

  if (!supabase) {
    return (
      <main>
        <div className="vv-page-header">
          <h1 className="vv-page-title">Mujeres</h1>
        </div>
        <div className="vv-empty">Error de configuracion.</div>
      </main>
    );
  }

  let items: Publicacion[] = [];
  let errorMsg: string | null = null;

  try {
    //  FILTRO CLAVE: solo categoria = "mujer"
    const { data: pubs, error: pubError } = await supabase
      .from("publicaciones")
      .select(
        "id, nombre, edad, zona, ciudad, cover_url, disponible, rating, created_at, user_id, categoria",
      )
      .eq("categoria", "mujer")
      .not("cover_url", "is", null)
      .neq("cover_url", "");

    if (pubError) {
      errorMsg = pubError.message;
    } else {
      const pubList = (pubs || []) as Publicacion[];
      const userIds = pubList.map((p) => p.user_id).filter(Boolean) as string[];

      let planMap: Record<string, string> = {};

      if (userIds.length > 0) {
        const { data: profiles } = await supabase
          .from("profiles")
          .select("id, plan_actual, plan_estado")
          .in("id", userIds);

        if (profiles) {
          for (const p of profiles) {
            planMap[p.id] =
              p.plan_estado === "activo" ? p.plan_actual || "free" : "free";
          }
        }
      }

      items = pubList.map((p) => ({
        ...p,
        plan_actual: p.user_id ? planMap[p.user_id] || "free" : "free",
      }));

      // Orden: plan (prioridad) > disponible > rating
      items.sort((a, b) => {
        const pa = getPlanConfig(a.plan_actual).ranking_priority;
        const pb = getPlanConfig(b.plan_actual).ranking_priority;
        if (pb !== pa) return pb - pa;

        const da = a.disponible !== false ? 1 : 0;
        const db = b.disponible !== false ? 1 : 0;
        if (db !== da) return db - da;

        return (b.rating || 0) - (a.rating || 0);
      });
    }
  } catch (err: unknown) {
    errorMsg = err instanceof Error ? err.message : "Error desconocido";
  }

  if (errorMsg) {
    return (
      <main>
        <div className="vv-page-header">
          <h1 className="vv-page-title">Mujeres</h1>
        </div>
        <div className="vv-empty">Error: {errorMsg}</div>
      </main>
    );
  }

  if (items.length === 0) {
    return (
      <main>
        <div className="vv-page-header">
          <h1 className="vv-page-title">Mujeres</h1>
        </div>
        <div className="vv-empty">No hay publicaciones aun.</div>
      </main>
    );
  }

  return (
    <main>
      <div className="vv-page-header">
        <h1 className="vv-page-title">Mujeres</h1>
        <p className="vv-page-subtitle">
          {items.length} perfil{items.length !== 1 ? "es" : ""}
        </p>
      </div>

      <div className="vv-grid">
        {items.map((item) => (
          <VVCard key={item.id} item={item} basePath="/mujeres" />
        ))}
      </div>
    </main>
  );
}

