import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

app/globals.css.content:
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;600;700;800&family=Inter:wght@300;400;500;600;700;800&display=swap');

*,
*::before,
*::after {
  box-sizing: border-box;
}

:root {
  --gold: #c6a75e;
  --gold-light: #e0d5b7;
  --gold-dark: #a8893f;
  --gold-gradient: linear-gradient(135deg, #c6a75e 0%, #a8893f 100%);
  --gold-gradient-light: linear-gradient(135deg, #e0d5b7 0%, #c6a75e 100%);
  --bg-primary: #0a0a0a;
  --bg-secondary: #111111;
  --bg-card: #141414;
  --bg-elevated: #1a1a1a;
  --border: #1e1e1e;
  --border-gold: rgba(198, 167, 94, 0.2);
  --text-primary: #ffffff;
  --text-secondary: #999999;
  --text-tertiary: #666666;
  --green-wa: #25d366;
}

body {
  font-family: 'Inter', system-ui, -apple-system, sans-serif;
  margin: 0;
  padding: 0;
  background: var(--bg-primary);
  color: var(--text-primary);
  -webkit-font-smoothing: antialiased;
  padding-bottom: 72px;
}

a {
  text-decoration: none;
  color: inherit;
}

/* ===== HEADER ===== */
.vv-header {
  position: sticky;
  top: 0;
  z-index: 200;
  background: var(--bg-primary);
  border-bottom: 1px solid var(--gold-dark);
}

.vv-header-main {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 56px;
  padding: 0 16px;
}

.vv-header-left {
  display: flex;
  align-items: center;
}

.vv-hamburger {
  background: none;
  border: none;
  color: var(--text-primary);
  cursor: pointer;
  padding: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.vv-hamburger svg {
  width: 22px;
  height: 22px;
}

.vv-logo {
  font-family: 'Playfair Display', serif;
  font-size: 22px;
  font-weight: 700;
  color: var(--gold);
  letter-spacing: 2px;
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
}

.vv-header-right {
  display: flex;
  align-items: center;
  gap: 8px;
}

.vv-header-icon-btn {
  background: none;
  border: none;
  color: var(--text-secondary);
  cursor: pointer;
  padding: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: color 0.15s;
}

.vv-header-icon-btn:hover {
  color: var(--gold);
}

.vv-header-icon-btn svg {
  width: 22px;
  height: 22px;
}

.vv-location-bar {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 6px 16px 8px;
  font-size: 12px;
  color: var(--text-secondary);
}

.vv-location-select {
  background: none;
  border: 1px solid var(--border);
  border-radius: 6px;
  color: var(--text-primary);
  font-size: 12px;
  padding: 4px 8px;
  font-family: inherit;
  cursor: pointer;
  appearance: auto;
}

.vv-location-select:focus {
  outline: none;
  border-color: var(--gold);
}

/* ===== SIDE MENU ===== */
.vv-overlay {
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.7);
  z-index: 300;
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.3s;
}

.vv-overlay-open {
  opacity: 1;
  pointer-events: auto;
}

.vv-side-menu {
  position: fixed;
  top: 0;
  left: -280px;
  width: 280px;
  height: 100%;
  background: var(--bg-primary);
  z-index: 301;
  transition: left 0.3s ease;
  display: flex;
  flex-direction: column;
  overflow-y: auto;
}

.vv-side-menu-open {
  left: 0;
}

.vv-side-menu-header {
  padding: 20px 16px;
  border-bottom: 1px solid rgba(198, 167, 94, 0.15);
}

.vv-side-menu-logo {
  font-family: 'Playfair Display', serif;
  font-size: 24px;
  font-weight: 700;
  color: var(--gold);
  letter-spacing: 2px;
}

.vv-side-menu-list {
  list-style: none;
  padding: 8px 0;
  margin: 0;
}

.vv-side-menu-item {
  display: block;
}

.vv-side-menu-link {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 14px 20px;
  font-size: 15px;
  color: var(--text-primary);
  transition: color 0.15s, background 0.15s;
  border-bottom: 1px solid rgba(198, 167, 94, 0.06);
}

.vv-side-menu-link:hover {
  color: var(--gold);
  background: rgba(198, 167, 94, 0.05);
}

.vv-side-menu-link svg {
  width: 18px;
  height: 18px;
  opacity: 0.5;
  flex-shrink: 0;
}

.vv-side-menu-close {
  position: absolute;
  top: 16px;
  right: 16px;
  background: none;
  border: none;
  color: var(--text-secondary);
  cursor: pointer;
  padding: 4px;
}

.vv-side-menu-close svg {
  width: 20px;
  height: 20px;
}

/* ===== BOTTOM NAV ===== */
.vv-bottom-nav {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 64px;
  background: var(--bg-primary);
  border-top: 1px solid var(--border);
  display: flex;
  align-items: center;
  justify-content: space-around;
  z-index: 200;
  padding-bottom: env(safe-area-inset-bottom, 0);
}

.vv-bottom-nav-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 3px;
  text-decoration: none;
  color: var(--text-tertiary);
  font-size: 10px;
  font-weight: 500;
  transition: color 0.15s;
  padding: 8px 12px;
  cursor: pointer;
  background: none;
  border: none;
  font-family: inherit;
}

.vv-bottom-nav-item:hover,
.vv-bottom-nav-item-active {
  color: var(--gold);
}

.vv-bottom-nav-item svg {
  width: 22px;
  height: 22px;
}

/* ===== FILTER PILLS BAR ===== */
.vv-filter-pills-bar {
  background: var(--bg-primary);
  border-bottom: 1px solid var(--border);
  padding: 0;
}

.vv-filter-pills-scroll {
  display: flex;
  gap: 8px;
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
  padding: 8px 16px;
  scrollbar-width: none;
}

.vv-filter-pills-scroll::-webkit-scrollbar { display: none; }

.vv-filter-pill {
  flex-shrink: 0;
  padding: 6px 16px;
  border-radius: 20px;
  background: var(--bg-elevated);
  border: 1px solid var(--border);
  color: var(--text-secondary);
  font-size: 13px;
  font-weight: 500;
  white-space: nowrap;
  transition: all 0.15s;
  cursor: pointer;
  text-decoration: none;
}

.vv-filter-pill:hover,
.vv-filter-pill-active {
  border-color: var(--gold);
  color: var(--gold);
  background: rgba(198, 167, 94, 0.08);
}

/* ===== HERO ===== */
.vv-hero {
  position: relative;
  padding: 48px 20px 40px;
  text-align: center;
  background: linear-gradient(180deg, rgba(198, 167, 94, 0.08) 0%, transparent 100%);
  overflow: hidden;
}

.vv-hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 300px;
  height: 300px;
  background: radial-gradient(circle, rgba(198, 167, 94, 0.06) 0%, transparent 70%);
  pointer-events: none;
}

.vv-hero-title {
  font-family: 'Playfair Display', serif;
  font-size: 26px;
  font-weight: 700;
  line-height: 1.3;
  margin: 0 0 8px;
  color: var(--text-primary);
  letter-spacing: -0.3px;
}

.vv-hero-subtitle {
  font-size: 14px;
  color: var(--text-secondary);
  margin: 0 0 24px;
  letter-spacing: 1px;
}

.vv-hero-btn {
  display: inline-block;
  background: var(--gold-gradient);
  color: #0a0a0a;
  font-size: 15px;
  font-weight: 700;
  padding: 14px 32px;
  border-radius: 8px;
  border: none;
  cursor: pointer;
  font-family: inherit;
  transition: opacity 0.15s;
  letter-spacing: 0.3px;
}

.vv-hero-btn:hover {
  opacity: 0.9;
}

.vv-city-filters {
  display: flex;
  justify-content: center;
  gap: 8px;
  margin-top: 20px;
  flex-wrap: wrap;
}

.vv-city-chip {
  background: var(--bg-elevated);
  border: 1px solid var(--border);
  border-radius: 20px;
  padding: 6px 16px;
  font-size: 12px;
  color: var(--text-secondary);
  cursor: pointer;
  transition: all 0.15s;
  font-family: inherit;
}

.vv-city-chip:hover,
.vv-city-chip-active {
  border-color: var(--gold);
  color: var(--gold);
  background: rgba(198, 167, 94, 0.08);
}

/* ===== INFO BLOCK (Limbo-style) ===== */
.vv-info-block {
  max-width: 640px;
  margin: 0 auto;
  padding: 20px 16px 12px;
  border-bottom: 1px solid var(--border);
}

.vv-info-cols {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 12px;
}

@media (max-width: 480px) {
  .vv-info-cols {
    grid-template-columns: 1fr;
    gap: 14px;
  }
}

.vv-info-col {
  text-align: center;
  padding: 8px 4px;
}

.vv-info-col-icon {
  width: 32px;
  height: 32px;
  margin: 0 auto 8px;
  color: var(--gold);
}

.vv-info-col-icon svg {
  width: 100%;
  height: 100%;
}

.vv-info-col-title {
  font-size: 13px;
  font-weight: 700;
  color: var(--text-primary);
  margin: 0 0 4px;
  line-height: 1.3;
}

.vv-info-col-text {
  font-size: 12px;
  color: var(--text-tertiary);
  margin: 0;
  line-height: 1.4;
}

.vv-info-expanded {
  margin-top: 14px;
  padding: 14px;
  background: var(--bg-card);
  border-radius: 8px;
  border: 1px solid var(--border);
}

.vv-info-expanded p {
  margin: 0;
  font-size: 13px;
  color: var(--text-secondary);
  line-height: 1.6;
}

.vv-info-toggle {
  display: block;
  margin: 12px auto 0;
  background: none;
  border: none;
  color: var(--gold);
  font-size: 13px;
  font-weight: 600;
  cursor: pointer;
  font-family: inherit;
  padding: 4px 12px;
}

/* ===== STORIES (Destacadas) ===== */
.vv-stories-section {
  border-bottom: 1px solid var(--border);
  padding-bottom: 16px;
}

.vv-stories-scroll {
  display: flex;
  gap: 14px;
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
  padding: 8px 16px 4px;
  scrollbar-width: none;
}

.vv-stories-scroll::-webkit-scrollbar { display: none; }

.vv-story-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 6px;
  flex-shrink: 0;
  text-decoration: none;
}

.vv-story-circle {
  width: 64px;
  height: 64px;
  border-radius: 50%;
  padding: 2px;
  background: linear-gradient(135deg, #e84393 0%, #c6a75e 50%, #e0d5b7 100%);
  overflow: hidden;
  flex-shrink: 0;
}

@media (min-width: 640px) {
  .vv-story-circle {
    width: 72px;
    height: 72px;
  }
}

.vv-story-img {
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
  display: block;
  border: 2px solid var(--bg-primary);
}

.vv-story-placeholder {
  width: 100%;
  height: 100%;
  border-radius: 50%;
  background: var(--bg-elevated);
  display: flex;
  align-items: center;
  justify-content: center;
  border: 2px solid var(--bg-primary);
}

.vv-story-placeholder svg {
  width: 28px;
  height: 28px;
  color: var(--text-tertiary);
}

.vv-story-name {
  font-size: 11px;
  color: var(--text-secondary);
  white-space: nowrap;
  max-width: 68px;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: center;
}

/* ===== SECTIONS ===== */
.vv-section {
  padding: 24px 16px 8px;
  max-width: 640px;
  margin: 0 auto;
}

.vv-section-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  margin-bottom: 14px;
}

.vv-section-title {
  font-family: 'Playfair Display', serif;
  font-size: 18px;
  font-weight: 700;
  margin: 0;
  color: var(--text-primary);
}

.vv-section-link {
  font-size: 13px;
  color: var(--gold);
  white-space: nowrap;
}

.vv-section-link:hover {
  text-decoration: underline;
}

/* ===== CARDS GRID ===== */
.vv-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 10px;
  padding: 0 16px 24px;
  max-width: 640px;
  margin: 0 auto;
}

@media (max-width: 400px) {
  .vv-grid {
    gap: 6px;
    padding: 0 8px 24px;
  }
}

.vv-card {
  position: relative;
  border-radius: 10px;
  overflow: hidden;
  background: var(--bg-card);
  cursor: pointer;
  transition: transform 0.15s ease;
}

.vv-card:active {
  transform: scale(0.98);
}

.vv-card-img-wrap {
  position: relative;
  width: 100%;
  aspect-ratio: 3 / 4;
  overflow: hidden;
}

.vv-card-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.vv-card-img-placeholder {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #1a1a1a 0%, #111 100%);
  color: #333;
  font-size: 48px;
}

.vv-card-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 40px 10px 10px;
  background: linear-gradient(to top, rgba(0, 0, 0, 0.85) 0%, rgba(0, 0, 0, 0.4) 60%, transparent 100%);
}

.vv-card-name {
  font-size: 14px;
  font-weight: 700;
  line-height: 1.2;
  margin: 0;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.vv-card-detail {
  font-size: 11px;
  color: rgba(255, 255, 255, 0.6);
  margin: 2px 0 0;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.vv-card-badge {
  position: absolute;
  top: 8px;
  left: 8px;
  font-size: 9px;
  font-weight: 700;
  padding: 3px 8px;
  border-radius: 4px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  line-height: 1;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.4);
}

.vv-card-badge-disponible {
  background: var(--green-wa);
  color: #fff;
}

.vv-card-badge-nueva {
  background: var(--gold-gradient);
  color: #0a0a0a;
}

.vv-card-plan-badge {
  position: absolute;
  bottom: 8px;
  left: 8px;
  font-size: 9px;
  font-weight: 700;
  padding: 3px 8px;
  border-radius: 4px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  line-height: 1;
}

.vv-card-heart {
  position: absolute;
  top: 8px;
  right: 8px;
  width: 30px;
  height: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(0, 0, 0, 0.4);
  border-radius: 50%;
  backdrop-filter: blur(4px);
  cursor: pointer;
  transition: background 0.15s;
}

.vv-card-heart:hover {
  background: rgba(198, 167, 94, 0.5);
}

.vv-card-heart svg {
  width: 14px;
  height: 14px;
  fill: none;
  stroke: #fff;
  stroke-width: 2;
}

.vv-card-rating {
  position: absolute;
  bottom: 10px;
  right: 10px;
  display: flex;
  align-items: center;
  gap: 3px;
  font-size: 11px;
  font-weight: 600;
  color: var(--gold);
}

.vv-card-rating svg {
  width: 11px;
  height: 11px;
  fill: var(--gold);
}

.vv-card-wa {
  position: absolute;
  bottom: 10px;
  right: 38px;
  width: 28px;
  height: 28px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--green-wa);
  border-radius: 50%;
  cursor: pointer;
  transition: transform 0.15s;
}

.vv-card-wa:hover {
  transform: scale(1.1);
}

.vv-card-wa svg {
  width: 14px;
  height: 14px;
  fill: #fff;
}

/* ===== HORIZONTAL SCROLL ===== */
.vv-scroll-row {
  display: flex;
  gap: 12px;
  overflow-x: auto;
  padding: 0 16px 16px;
  max-width: 640px;
  margin: 0 auto;
  scroll-snap-type: x mandatory;
  -webkit-overflow-scrolling: touch;
}

.vv-scroll-row::-webkit-scrollbar {
  display: none;
}

.vv-scroll-card {
  flex: 0 0 160px;
  scroll-snap-align: start;
}

/* ===== PAGE HEADERS ===== */
.vv-page-header {
  padding: 24px 16px 8px;
  max-width: 640px;
  margin: 0 auto;
}

.vv-page-title {
  font-family: 'Playfair Display', serif;
  font-size: 22px;
  font-weight: 700;
  margin: 0 0 4px;
  letter-spacing: -0.3px;
}

.vv-page-subtitle {
  font-size: 13px;
  color: var(--text-secondary);
  margin: 0;
}

/* ===== FORM STYLES ===== */
.vv-form-page {
  min-height: 100vh;
  display: flex;
  justify-content: center;
  padding: 24px 16px 80px;
}

.vv-form-container {
  width: 100%;
  max-width: 480px;
}

.vv-form-header {
  margin-bottom: 24px;
}

.vv-form-title {
  font-family: 'Playfair Display', serif;
  font-size: 22px;
  font-weight: 700;
  margin: 0 0 4px;
  letter-spacing: -0.3px;
}

.vv-form-subtitle {
  font-size: 13px;
  color: var(--text-secondary);
  margin: 0;
}

.vv-form-loading {
  color: var(--text-secondary);
  font-size: 14px;
  text-align: center;
  padding: 60px 0;
}

.vv-form-error-box {
  background: rgba(220, 38, 38, 0.1);
  border: 1px solid rgba(220, 38, 38, 0.2);
  color: #f87171;
  padding: 10px 14px;
  border-radius: 8px;
  font-size: 13px;
  margin-bottom: 16px;
}

.vv-form-success-box {
  background: rgba(34, 197, 94, 0.1);
  border: 1px solid rgba(34, 197, 94, 0.2);
  color: #4ade80;
  padding: 10px 14px;
  border-radius: 8px;
  font-size: 13px;
  margin-bottom: 16px;
}

.vv-form-link {
  display: inline-block;
  color: var(--gold);
  font-size: 14px;
  font-weight: 600;
  margin-top: 8px;
}

.vv-form-link:hover {
  text-decoration: underline;
}

.vv-form {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.vv-field {
  display: flex;
  flex-direction: column;
  gap: 6px;
  flex: 1;
}

.vv-field-row {
  display: flex;
  gap: 12px;
}

.vv-label {
  font-size: 13px;
  font-weight: 600;
  color: var(--text-secondary);
}

.vv-input,
.vv-textarea,
.vv-select {
  background: var(--bg-elevated);
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 10px 12px;
  font-size: 14px;
  color: var(--text-primary);
  outline: none;
  transition: border-color 0.15s;
  font-family: inherit;
  width: 100%;
}

.vv-input:focus,
.vv-textarea:focus,
.vv-select:focus {
  border-color: var(--gold);
}

.vv-input::placeholder,
.vv-textarea::placeholder {
  color: #444;
}

.vv-textarea {
  resize: vertical;
  min-height: 80px;
}

.vv-field-check {
  display: flex;
  align-items: center;
  gap: 8px;
}

.vv-checkbox {
  width: 18px;
  height: 18px;
  accent-color: var(--gold);
  cursor: pointer;
}

.vv-label-check {
  font-size: 14px;
  color: var(--text-secondary);
  cursor: pointer;
}

.vv-btn {
  background: var(--gold-gradient);
  color: #0a0a0a;
  border: none;
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 15px;
  font-weight: 700;
  cursor: pointer;
  transition: opacity 0.15s;
  margin-top: 8px;
  font-family: inherit;
}

.vv-btn:hover {
  opacity: 0.9;
}

.vv-btn:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

.vv-btn-secondary {
  background: var(--bg-elevated);
  color: var(--text-primary);
  border: 1px solid var(--border);
}

.vv-btn-secondary:hover {
  border-color: var(--gold);
}

.vv-btn-wa {
  background: var(--green-wa);
  color: #fff;
}

.vv-form-back {
  display: inline-block;
  color: var(--text-tertiary);
  font-size: 13px;
  margin-top: 20px;
}

.vv-form-back:hover {
  color: var(--text-primary);
}

.vv-auth-links {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: 16px;
}

.vv-text-btn {
  background: none;
  border: none;
  color: var(--gold);
  font-size: 13px;
  font-weight: 600;
  cursor: pointer;
  padding: 0;
  text-align: left;
  font-family: inherit;
}

.vv-text-btn:hover {
  text-decoration: underline;
}

/* ===== REGISTRATION / VERIFICATION ===== */
.vv-progress {
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 20px;
}

.vv-progress-bar {
  flex: 1;
  height: 4px;
  background: #222;
  border-radius: 2px;
  overflow: hidden;
}

.vv-progress-fill {
  height: 100%;
  background: #22c55e;
  border-radius: 2px;
  transition: width 0.3s ease;
}

.vv-progress-text {
  font-size: 12px;
  color: var(--text-secondary);
  white-space: nowrap;
}

.vv-welcome {
  text-align: center;
}

.vv-welcome-icon {
  margin: 0 auto 20px;
  width: 80px;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(34, 197, 94, 0.1);
  border-radius: 50%;
}

.vv-welcome-text {
  font-size: 15px;
  color: var(--text-secondary);
  line-height: 1.6;
  margin: 12px 0 24px;
}

.vv-welcome-steps {
  display: flex;
  flex-direction: column;
  gap: 12px;
  text-align: left;
  margin: 0 0 24px;
}

.vv-welcome-step {
  display: flex;
  align-items: center;
  gap: 12px;
  font-size: 14px;
  color: #ddd;
}

.vv-welcome-step-num {
  width: 28px;
  height: 28px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(34, 197, 94, 0.15);
  color: #22c55e;
  border-radius: 50%;
  font-size: 13px;
  font-weight: 700;
  flex-shrink: 0;
}

.vv-welcome-privacy {
  font-size: 12px;
  color: var(--text-tertiary);
  margin: 0 0 20px;
}

.vv-doc-options {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.vv-doc-option {
  display: flex;
  align-items: center;
  gap: 12px;
  background: var(--bg-elevated);
  border: 1px solid var(--border);
  border-radius: 10px;
  padding: 14px 16px;
  font-size: 15px;
  color: #ddd;
  cursor: pointer;
  font-family: inherit;
  text-align: left;
  transition: border-color 0.15s, background 0.15s;
}

.vv-doc-option:hover {
  background: #1e1e1e;
}

.vv-doc-option-selected {
  border-color: #22c55e;
  background: rgba(34, 197, 94, 0.05);
}

.vv-doc-option-radio {
  font-size: 18px;
  color: #555;
  line-height: 1;
}

.vv-doc-option-selected .vv-doc-option-radio {
  color: #22c55e;
}

.vv-camera-wrapper {
  position: relative;
  width: 100%;
  aspect-ratio: 16 / 10;
  background: #000;
  border-radius: 12px;
  overflow: hidden;
}

.vv-camera-selfie {
  aspect-ratio: 1 / 1;
  max-width: 320px;
  margin: 0 auto;
  border-radius: 50%;
}

.vv-camera-video {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.vv-btn-verify {
  background: #22c55e;
}

.vv-btn-verify:hover {
  opacity: 0.9;
}

/* ===== PROFILE PAGE ===== */
.vv-perfil-page {
  min-height: 100vh;
  display: flex;
  justify-content: center;
  padding: 0 0 80px;
}

.vv-perfil-container {
  width: 100%;
  max-width: 480px;
}

.vv-perfil-back {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  color: var(--text-secondary);
  font-size: 14px;
  padding: 14px 16px;
}

.vv-perfil-back:hover {
  color: var(--gold);
}

.vv-perfil-img-wrap {
  position: relative;
  width: 100%;
  aspect-ratio: 3 / 4;
  overflow: hidden;
}

.vv-perfil-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.vv-perfil-top-info {
  padding: 16px;
}

.vv-perfil-name-row {
  display: flex;
  align-items: center;
  gap: 10px;
  flex-wrap: wrap;
  margin-bottom: 8px;
}

.vv-perfil-name {
  font-family: 'Playfair Display', serif;
  font-size: 22px;
  font-weight: 700;
  margin: 0;
}

.vv-perfil-plan-badge {
  font-size: 10px;
  font-weight: 700;
  padding: 3px 10px;
  border-radius: 4px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.vv-perfil-meta {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 12px;
  margin-bottom: 12px;
  font-size: 13px;
  color: var(--text-secondary);
}

.vv-perfil-meta-item {
  display: flex;
  align-items: center;
  gap: 4px;
}

.vv-perfil-meta-item svg {
  width: 14px;
  height: 14px;
}

.vv-perfil-rating {
  display: flex;
  align-items: center;
  gap: 4px;
  color: var(--gold);
  font-weight: 600;
}

.vv-perfil-rating svg {
  width: 14px;
  height: 14px;
  fill: var(--gold);
}

.vv-perfil-actions {
  display: flex;
  gap: 8px;
  padding: 0 16px 16px;
}

.vv-perfil-actions .vv-btn {
  flex: 1;
  text-align: center;
  margin: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
}

.vv-perfil-actions .vv-btn svg {
  width: 16px;
  height: 16px;
}

/* ===== TABS ===== */
.vv-tabs {
  display: flex;
  border-bottom: 1px solid var(--border);
  position: sticky;
  top: 57px;
  background: var(--bg-primary);
  z-index: 50;
}

.vv-tab {
  flex: 1;
  text-align: center;
  padding: 12px 8px;
  font-size: 13px;
  font-weight: 600;
  color: var(--text-tertiary);
  cursor: pointer;
  border-bottom: 2px solid transparent;
  background: none;
  border-top: none;
  border-left: none;
  border-right: none;
  font-family: inherit;
  transition: color 0.15s;
}

.vv-tab:hover {
  color: var(--text-secondary);
}

.vv-tab-active {
  color: var(--gold);
  border-bottom-color: var(--gold);
}

.vv-tab-content {
  padding: 16px;
}

/* ===== PROFILE INFO TAB ===== */
.vv-info-row {
  display: flex;
  justify-content: space-between;
  padding: 10px 0;
  border-bottom: 1px solid rgba(255,255,255,0.04);
}

.vv-info-key {
  font-size: 13px;
  color: var(--text-secondary);
}

.vv-info-val {
  font-size: 13px;
  color: var(--text-primary);
  font-weight: 500;
}

.vv-perfil-desc {
  font-size: 14px;
  line-height: 1.7;
  color: var(--text-secondary);
  margin: 12px 0 0;
  white-space: pre-wrap;
}

/* ===== SERVICES CHIPS ===== */
.vv-chips {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.vv-chip {
  background: rgba(198, 167, 94, 0.08);
  border: 1px solid rgba(198, 167, 94, 0.15);
  color: var(--gold-light);
  font-size: 12px;
  font-weight: 600;
  padding: 6px 14px;
  border-radius: 20px;
  white-space: nowrap;
}

/* ===== GALLERY ===== */
.vv-gallery {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 4px;
}

.vv-gallery-item {
  aspect-ratio: 1;
  overflow: hidden;
  border-radius: 4px;
  cursor: pointer;
}

.vv-gallery-item img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s;
}

.vv-gallery-item:hover img {
  transform: scale(1.05);
}

/* ===== REVIEWS ===== */
.vv-review {
  padding: 14px 0;
  border-bottom: 1px solid rgba(255,255,255,0.04);
}

.vv-review-header {
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 6px;
}

.vv-review-stars {
  display: flex;
  gap: 2px;
}

.vv-review-stars svg {
  width: 12px;
  height: 12px;
  fill: var(--gold);
}

.vv-review-date {
  font-size: 11px;
  color: var(--text-tertiary);
}

.vv-review-text {
  font-size: 13px;
  color: var(--text-secondary);
  line-height: 1.5;
}

/* ===== ADMIN ===== */
.vv-admin-container {
  width: 100%;
  max-width: 800px;
}

.vv-admin-list {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.vv-admin-card {
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 16px;
}

.vv-admin-card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 8px;
  margin-bottom: 8px;
}

.vv-admin-email {
  font-size: 14px;
  font-weight: 600;
  color: var(--text-primary);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.vv-admin-status {
  font-size: 11px;
  font-weight: 700;
  text-transform: uppercase;
  padding: 3px 10px;
  border-radius: 20px;
  letter-spacing: 0.5px;
  white-space: nowrap;
}

.vv-admin-status-en_revision { background: rgba(251, 191, 36, 0.15); color: #fbbf24; }
.vv-admin-status-pendiente { background: rgba(136, 136, 136, 0.15); color: #888; }
.vv-admin-status-aprobado { background: rgba(34, 197, 94, 0.15); color: #22c55e; }
.vv-admin-status-rechazado { background: rgba(220, 38, 38, 0.15); color: #f87171; }

.vv-admin-detail {
  font-size: 12px;
  color: var(--text-secondary);
  margin: 4px 0;
}

.vv-admin-images {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 8px;
  margin-top: 12px;
}

.vv-admin-img-box {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.vv-admin-img-label {
  font-size: 11px;
  color: var(--text-tertiary);
  margin: 0;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.vv-admin-img {
  width: 100%;
  aspect-ratio: 4 / 3;
  object-fit: cover;
  border-radius: 6px;
  background: #111;
}

.vv-admin-actions {
  display: flex;
  gap: 8px;
  margin-top: 12px;
}

@media (max-width: 500px) {
  .vv-admin-images {
    grid-template-columns: 1fr 1fr;
  }
}

.vv-btn-approve { background: #22c55e; flex: 1; }
.vv-btn-approve:hover { opacity: 0.9; }
.vv-btn-reject { background: #555; flex: 1; color: #fff; }
.vv-btn-reject:hover { background: #666; }

/* ===== PLANS ===== */
.vv-planes-container {
  width: 100%;
  max-width: 900px;
}

.vv-planes-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 12px;
}

@media (max-width: 700px) {
  .vv-planes-grid { grid-template-columns: 1fr; }
}

.vv-plan-card {
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 24px 16px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
  transition: border-color 0.2s;
  position: relative;
  overflow: hidden;
}

.vv-plan-card-featured {
  border-color: var(--gold);
}

.vv-plan-card-featured::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: var(--gold-gradient);
}

.vv-plan-badge {
  font-size: 12px;
  font-weight: 700;
  padding: 4px 16px;
  border-radius: 20px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  white-space: nowrap;
}

.vv-plan-price {
  text-align: center;
}

.vv-plan-price-amount {
  font-family: 'Playfair Display', serif;
  font-size: 32px;
  font-weight: 800;
  letter-spacing: -0.5px;
}

.vv-plan-price-period {
  font-size: 13px;
  color: var(--text-secondary);
  margin-left: 2px;
}

.vv-plan-features {
  list-style: none;
  padding: 0;
  margin: 0;
  width: 100%;
}

.vv-plan-feature {
  display: flex;
  align-items: flex-start;
  gap: 8px;
  font-size: 13px;
  color: var(--text-secondary);
  padding: 4px 0;
  line-height: 1.4;
}

.vv-plan-check {
  width: 16px;
  height: 16px;
  flex-shrink: 0;
  margin-top: 1px;
}

.vv-plan-btn {
  width: 100%;
  text-align: center;
}

.vv-plan-current {
  width: 100%;
  text-align: center;
  font-size: 13px;
  font-weight: 600;
  color: var(--gold);
  padding: 12px;
  background: rgba(198, 167, 94, 0.08);
  border-radius: 8px;
}

.vv-planes-note {
  text-align: center;
  font-size: 12px;
  color: var(--text-tertiary);
  margin-top: 24px;
}

/* ===== MI CUENTA ===== */
.vv-cuenta-section {
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 16px;
  margin-bottom: 16px;
}

.vv-cuenta-label {
  font-size: 12px;
  font-weight: 700;
  color: var(--gold);
  text-transform: uppercase;
  letter-spacing: 1px;
  margin: 0 0 12px;
}

.vv-cuenta-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 8px;
}

.vv-cuenta-key {
  font-size: 13px;
  color: var(--text-secondary);
}

.vv-cuenta-val {
  font-size: 13px;
  color: var(--text-primary);
  font-weight: 500;
}

.vv-cuenta-plan-card {
  background: var(--bg-secondary);
  border: 1px solid var(--border);
  border-radius: 10px;
  padding: 14px;
}

.vv-cuenta-plan-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 8px;
}

.vv-cuenta-plan-estado { font-size: 12px; font-weight: 600; }
.vv-cuenta-plan-activo { color: #4ade80; }
.vv-cuenta-plan-vencido { color: #f87171; }

.vv-cuenta-plan-limits {
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
  margin-top: 12px;
  font-size: 12px;
  color: var(--text-secondary);
}

.vv-cuenta-actions {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.vv-cuenta-action-link {
  font-size: 14px;
  color: var(--gold);
  font-weight: 600;
}

.vv-cuenta-action-link:hover {
  text-decoration: underline;
}

/* ===== METRICS ===== */
.vv-metrics-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 10px;
}

@media (max-width: 400px) {
  .vv-metrics-grid { grid-template-columns: 1fr; }
}

.vv-metric-card {
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 10px;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.vv-metric-label {
  font-size: 11px;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.vv-metric-value {
  font-family: 'Playfair Display', serif;
  font-size: 28px;
  font-weight: 800;
}

.vv-metric-up { color: #4ade80; }
.vv-metric-down { color: #f87171; }

.vv-metrics-table-wrap {
  overflow-x: auto;
}

.vv-metrics-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 13px;
  margin-top: 8px;
}

.vv-metrics-table th {
  text-align: left;
  padding: 8px 10px;
  color: var(--text-secondary);
  border-bottom: 1px solid var(--border);
  font-weight: 600;
  font-size: 11px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.vv-metrics-table td {
  padding: 8px 10px;
  color: var(--text-secondary);
  border-bottom: 1px solid rgba(255,255,255,0.03);
}

.vv-metrics-table tr:hover td {
  background: rgba(198, 167, 94, 0.03);
}

/* ===== PLAN LIMITS INFO ===== */
.vv-plan-limits-info {
  display: flex;
  align-items: center;
  gap: 12px;
  flex-wrap: wrap;
  padding: 10px 14px;
  background: rgba(198, 167, 94, 0.05);
  border: 1px solid rgba(198, 167, 94, 0.15);
  border-radius: 8px;
  font-size: 12px;
  color: var(--text-secondary);
  margin-bottom: 16px;
}

/* ===== EMPTY STATE ===== */
.vv-empty {
  text-align: center;
  color: var(--text-secondary);
  font-size: 14px;
  padding: 60px 16px;
}

/* ===== FEATURED CAROUSEL ===== */
.vv-featured-section {
  padding: 24px 0 8px;
  max-width: 640px;
  margin: 0 auto;
  background: linear-gradient(180deg, rgba(198, 167, 94, 0.04) 0%, transparent 100%);
  border-radius: 12px;
}

.vv-featured-section .vv-section-header {
  padding: 0 16px;
}

/* ===== PROFILE VIEW (vvp-) Limbo-style ===== */
.vvp-page {
  min-height: 100vh;
  display: flex;
  justify-content: center;
  padding: 0;
  background: var(--bg-primary);
}

.vvp-container {
  width: 100%;
  max-width: 480px;
  position: relative;
}

.vvp-back-btn {
  position: absolute;
  top: 14px;
  left: 12px;
  z-index: 60;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 50%;
  color: #fff;
  backdrop-filter: blur(8px);
  transition: transform 0.1s;
}

.vvp-back-btn:active { transform: scale(0.92); }

.vvp-back-btn svg { width: 18px; height: 18px; }

.vvp-fav-btn {
  position: absolute;
  top: 14px;
  right: 12px;
  z-index: 60;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 50%;
  border: none;
  cursor: pointer;
  backdrop-filter: blur(8px);
  transition: transform 0.15s;
}

.vvp-fav-btn:active { transform: scale(1.2); }

.vvp-fav-btn svg { width: 20px; height: 20px; }

/* -- Cover image (full width, Limbo-style) -- */
.vvp-cover {
  position: relative;
  width: 100%;
  aspect-ratio: 3 / 4;
  overflow: hidden;
  cursor: pointer;
  max-height: 520px;
}

.vvp-cover-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.vvp-cover-placeholder {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #1a1a1a 0%, #111 100%);
}

.vvp-cover-placeholder svg {
  width: 64px;
  height: 64px;
  color: #333;
}

.vvp-cover-gradient {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 60%;
  background: linear-gradient(to top, rgba(0, 0, 0, 0.85) 0%, rgba(0, 0, 0, 0.4) 50%, transparent 100%);
  pointer-events: none;
}

.vvp-cover-info {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 16px;
  z-index: 2;
}

.vvp-cover-name-row {
  display: flex;
  align-items: baseline;
  gap: 8px;
  flex-wrap: wrap;
}

.vvp-cover-name {
  font-family: 'Playfair Display', serif;
  font-size: 26px;
  font-weight: 700;
  margin: 0;
  color: #fff;
  line-height: 1.2;
  text-shadow: 0 1px 8px rgba(0,0,0,0.5);
}

.vvp-cover-age {
  font-size: 22px;
  font-weight: 600;
  color: rgba(255,255,255,0.8);
}

.vvp-cover-meta {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-top: 6px;
  flex-wrap: wrap;
}

.vvp-cover-loc {
  display: flex;
  align-items: center;
  gap: 4px;
  font-size: 13px;
  color: rgba(255,255,255,0.7);
}

.vvp-cover-loc svg { width: 14px; height: 14px; }

.vvp-cover-rating {
  display: flex;
  align-items: center;
  gap: 3px;
  font-size: 13px;
  font-weight: 700;
  color: var(--gold);
}

.vvp-cover-rating svg {
  width: 14px;
  height: 14px;
  fill: var(--gold);
}

.vvp-cover-online {
  display: flex;
  align-items: center;
  gap: 4px;
  font-size: 12px;
  font-weight: 600;
  color: #25d366;
}

.vvp-cover-online-dot {
  width: 7px;
  height: 7px;
  background: #25d366;
  border-radius: 50%;
  animation: vvpPulse 2s ease-in-out infinite;
}

@keyframes vvpPulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.4; }
}

.vvp-cover-plan {
  display: inline-block;
  font-size: 9px;
  font-weight: 800;
  padding: 3px 10px;
  border-radius: 4px;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  margin-top: 8px;
}

/* -- Action Buttons (WhatsApp + Llamar) -- */
.vvp-actions {
  display: flex;
  gap: 8px;
  padding: 10px 12px;
}

.vvp-act-btn {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  padding: 11px 12px;
  border-radius: 10px;
  font-size: 14px;
  font-weight: 700;
  font-family: inherit;
  cursor: pointer;
  border: none;
  text-decoration: none;
  transition: transform 0.1s, filter 0.1s;
}

.vvp-act-btn:active {
  transform: scale(0.96);
  filter: brightness(0.85);
}

.vvp-act-btn svg { width: 18px; height: 18px; }

.vvp-act-wa {
  background: #25d366;
  color: #fff;
}

.vvp-act-call {
  background: var(--bg-elevated);
  color: var(--text-primary);
  border: 1px solid var(--border);
}

.vvp-act-disabled {
  opacity: 0.35;
  cursor: not-allowed;
  pointer-events: none;
}

/* -- Mini actions (Report, Share) -- */
.vvp-mini-actions {
  display: flex;
  justify-content: center;
  gap: 24px;
  padding: 6px 12px 10px;
  border-bottom: 1px solid var(--border);
}

.vvp-mini-act {
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 11px;
  color: var(--text-tertiary);
  background: none;
  border: none;
  cursor: pointer;
  font-family: inherit;
  padding: 4px 8px;
  transition: color 0.15s;
}

.vvp-mini-act:hover { color: var(--text-secondary); }

.vvp-mini-act svg { width: 15px; height: 15px; }

/* -- Tabs (thinner, Limbo-style) -- */
.vvp-tabs {
  display: flex;
  background: var(--bg-primary);
  border-bottom: 1px solid var(--border);
  position: sticky;
  top: 57px;
  z-index: 55;
}

.vvp-tab {
  flex: 1;
  text-align: center;
  padding: 10px 8px;
  font-size: 13px;
  font-weight: 600;
  color: var(--text-tertiary);
  cursor: pointer;
  border: none;
  border-bottom: 2px solid transparent;
  background: none;
  font-family: inherit;
  transition: color 0.15s, border-color 0.15s;
}

.vvp-tab-active {
  color: var(--gold);
  border-bottom-color: var(--gold);
}

/* -- Sections -- */
.vvp-section {
  padding: 16px 14px 8px;
  border-bottom: 1px solid var(--border);
}

.vvp-section-title {
  font-family: 'Playfair Display', serif;
  font-size: 15px;
  font-weight: 700;
  margin: 0 0 10px;
  color: var(--text-primary);
}

.vvp-presentacion {
  font-size: 14px;
  line-height: 1.75;
  color: var(--text-secondary);
  margin: 0;
  white-space: pre-wrap;
}

/* -- Gallery (horizontal scroll) -- */
.vvp-gallery-scroll {
  display: flex;
  gap: 8px;
  overflow-x: auto;
  padding-bottom: 12px;
  -webkit-overflow-scrolling: touch;
  scrollbar-width: none;
}

.vvp-gallery-scroll::-webkit-scrollbar {
  display: none;
}

.vvp-gallery-thumb {
  flex: 0 0 auto;
  width: 120px;
  height: 160px;
  border-radius: 10px;
  overflow: hidden;
  position: relative;
}

.vvp-gallery-thumb img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

.vvp-gallery-placeholder {
  background: var(--bg-elevated);
  border: 1px dashed var(--border);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 6px;
  color: var(--text-tertiary);
  font-size: 10px;
}

.vvp-gallery-placeholder svg {
  width: 24px;
  height: 24px;
}

/* -- Info Grid -- */
.vvp-info-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1px;
  background: var(--border);
  border-radius: 10px;
  overflow: hidden;
  margin-bottom: 20px;
}

.vvp-info-item {
  background: var(--bg-card);
  padding: 12px 14px;
  display: flex;
  flex-direction: column;
  gap: 3px;
}

.vvp-info-label {
  font-size: 10px;
  font-weight: 600;
  color: var(--text-tertiary);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.vvp-info-value {
  font-size: 13px;
  font-weight: 500;
  color: var(--text-primary);
}

/* -- Service Chips (Limbo-style pill buttons) -- */
.vvp-service-group {
  margin-bottom: 18px;
}

.vvp-service-group-title {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 13px;
  font-weight: 700;
  color: var(--text-secondary);
  margin: 0 0 10px;
  font-family: 'Inter', sans-serif;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.vvp-service-group-title svg {
  width: 16px;
  height: 16px;
  color: var(--gold);
}

.vvp-chips {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.vvp-chip {
  background: rgba(198, 167, 94, 0.06);
  border: 1px solid rgba(198, 167, 94, 0.15);
  color: var(--gold-light);
  font-size: 12px;
  font-weight: 500;
  padding: 7px 16px;
  border-radius: 50px;
  white-space: nowrap;
}

/* -- Opiniones -- */
.vvp-opiniones-empty {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 12px;
  padding: 24px 16px;
  color: var(--text-tertiary);
}

.vvp-opiniones-empty svg {
  width: 36px;
  height: 36px;
  opacity: 0.3;
}

.vvp-opiniones-empty p {
  margin: 0;
  font-size: 13px;
}

/* -- Comment Form -- */
.vvp-comment-form {
  display: flex;
  gap: 8px;
  margin-bottom: 16px;
}

.vvp-comment-input {
  flex: 1;
  background: var(--bg-elevated);
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 8px 10px;
  font-size: 13px;
  color: var(--text-primary);
  font-family: inherit;
  resize: none;
  outline: none;
}

.vvp-comment-input:focus { border-color: var(--gold); }

.vvp-comment-input::placeholder { color: #444; }

.vvp-comment-submit {
  padding: 8px 16px;
  background: var(--gold-gradient);
  color: #0a0a0a;
  border: none;
  border-radius: 8px;
  font-size: 13px;
  font-weight: 700;
  cursor: pointer;
  font-family: inherit;
  white-space: nowrap;
  align-self: flex-end;
  transition: opacity 0.15s;
}

.vvp-comment-submit:disabled { opacity: 0.4; cursor: not-allowed; }

.vvp-comments-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.vvp-comment-item {
  padding: 10px 0;
  border-bottom: 1px solid rgba(255,255,255,0.04);
}

.vvp-comment-header {
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 4px;
}

.vvp-comment-avatar {
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: var(--bg-elevated);
  display: flex;
  align-items: center;
  justify-content: center;
}

.vvp-comment-avatar svg { width: 14px; height: 14px; color: var(--text-tertiary); }

.vvp-comment-date { font-size: 11px; color: var(--text-tertiary); }

.vvp-comment-text {
  font-size: 13px;
  color: var(--text-secondary);
  line-height: 1.5;
  margin: 0;
}

/* -- Sticky WhatsApp Bottom -- */
.vvp-sticky-wa {
  position: fixed;
  bottom: 62px;
  left: 0;
  right: 0;
  z-index: 80;
  display: flex;
  justify-content: center;
  padding: 0 16px 12px;
  pointer-events: none;
}

.vvp-sticky-wa-btn {
  max-width: 448px;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  padding: 14px 24px;
  background: #25d366;
  color: #fff;
  font-size: 15px;
  font-weight: 700;
  font-family: inherit;
  border: none;
  border-radius: 14px;
  cursor: pointer;
  pointer-events: auto;
  box-shadow: 0 4px 20px rgba(37, 211, 102, 0.3);
  text-decoration: none;
  transition: transform 0.1s, filter 0.1s;
}

.vvp-sticky-wa-btn:active {
  transform: scale(0.97);
  filter: brightness(0.85);
}

.vvp-sticky-wa-btn svg { width: 20px; height: 20px; }

/* -- Scroll to Top -- */
.vvp-scroll-top {
  position: fixed;
  bottom: 135px;
  right: 16px;
  z-index: 80;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: var(--bg-elevated);
  border: 1px solid var(--border);
  color: var(--gold);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  box-shadow: 0 2px 12px rgba(0,0,0,0.4);
}

.vvp-scroll-top svg { width: 20px; height: 20px; }

.vvp-no-services {
  color: var(--text-tertiary);
  font-size: 13px;
  margin-top: 12px;
  font-style: italic;
}

/* -- Lightbox -- */
.vvp-lightbox {
  position: fixed;
  inset: 0;
  z-index: 500;
  background: rgba(0, 0, 0, 0.95);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
}

.vvp-lightbox-img {
  max-width: 95vw;
  max-height: 90vh;
  object-fit: contain;
  border-radius: 4px;
  cursor: default;
}

.vvp-lightbox-close {
  position: absolute;
  top: 16px;
  right: 16px;
  width: 40px;
  height: 40px;
  background: rgba(255,255,255,0.1);
  border: none;
  border-radius: 50%;
  color: #fff;
  font-size: 24px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 501;
}

/* -- Report / Modal -- */
.vvp-modal-overlay {
  position: fixed;
  inset: 0;
  z-index: 400;
  background: rgba(0, 0, 0, 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
}

.vvp-modal {
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 14px;
  padding: 20px;
  width: 100%;
  max-width: 400px;
}

.vvp-modal-title {
  font-family: 'Playfair Display', serif;
  font-size: 18px;
  font-weight: 700;
  margin: 0 0 16px;
  color: var(--text-primary);
}

.vvp-report-sent {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 12px;
  padding: 24px 0;
}

.vvp-report-sent svg { width: 40px; height: 40px; }

.vvp-report-sent p {
  margin: 0;
  font-size: 15px;
  color: #22c55e;
  font-weight: 600;
}

/* ===== PUBLICAR STEPPER ===== */
.vv-stepper {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0;
  margin-bottom: 28px;
}

.vv-step {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 500;
  color: var(--text-tertiary);
  background: transparent;
  border: 1px solid var(--border);
  cursor: pointer;
  transition: color 0.2s, border-color 0.2s;
}

.vv-step-active {
  color: var(--gold);
  border-color: var(--gold);
  background: rgba(198, 167, 94, 0.08);
}

.vv-step-done {
  color: var(--text-secondary);
  border-color: var(--gold-dark);
}

.vv-step-num {
  width: 24px;
  height: 24px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 12px;
  font-weight: 700;
  background: var(--bg-elevated);
  border: 1px solid var(--border);
}

.vv-step-active .vv-step-num {
  background: var(--gold);
  color: #000;
  border-color: var(--gold);
}

.vv-step-done .vv-step-num {
  background: var(--gold-dark);
  color: #000;
  border-color: var(--gold-dark);
}

.vv-step-connector {
  width: 32px;
  height: 1px;
  background: var(--border);
  flex-shrink: 0;
}

.vv-form-nav {
  display: flex;
  gap: 12px;
  margin-top: 24px;
}

.vv-btn-secondary {
  flex: 1;
  padding: 14px;
  border-radius: 8px;
  font-size: 15px;
  font-weight: 600;
  cursor: pointer;
  background: var(--bg-elevated);
  color: var(--text-primary);
  border: 1px solid var(--border);
  text-align: center;
}

.vv-chip-selector {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 6px;
}

.vv-chip-option {
  padding: 7px 14px;
  border-radius: 20px;
  font-size: 13px;
  font-weight: 500;
  cursor: pointer;
  border: 1px solid var(--border);
  background: var(--bg-card);
  color: var(--text-secondary);
  transition: border-color 0.15s, color 0.15s, background 0.15s;
}

.vv-chip-option-selected {
  border-color: var(--gold);
  color: var(--gold);
  background: rgba(198, 167, 94, 0.12);
}

.vv-field-inline {
  display: flex;
  align-items: center;
  gap: 10px;
}

.vv-field-inline .vv-label-check {
  flex: 1;
}

vite.config.ts.content:
import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

client/src/components/ui/alert.tsx.content:
import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const alertVariants = cva(
  "relative w-full rounded-lg border p-4 [&>svg~*]:pl-7 [&>svg+div]:translate-y-[-3px] [&>svg]:absolute [&>svg]:left-4 [&>svg]:top-4 [&>svg]:text-foreground",
  {
    variants: {
      variant: {
        default: "bg-background text-foreground",
        destructive:
          "border-destructive/50 text-destructive dark:border-destructive [&>svg]:text-destructive",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

const Alert = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & VariantProps<typeof alertVariants>
>(({ className, variant, ...props }, ref) => (
  <div
    ref={ref}
    role="alert"
    className={cn(alertVariants({ variant }), className)}
    {...props}
  />
))
Alert.displayName = "Alert"

const AlertTitle = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLHeadingElement>
>(({ className, ...props }, ref) => (
  <h5
    ref={ref}
    className={cn("mb-1 font-medium leading-none tracking-tight", className)}
    {...props}
  />
))
AlertTitle.displayName = "AlertTitle"

const AlertDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("text-sm [&_p]:leading-relaxed", className)}
    {...props}
  />
))
AlertDescription.displayName = "AlertDescription"

export { Alert, AlertTitle, AlertDescription }

client/src/components/ui/breadcrumb.tsx.content:
import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { ChevronRight, MoreHorizontal } from "lucide-react"

import { cn } from "@/lib/utils"

const Breadcrumb = React.forwardRef<
  HTMLElement,
  React.ComponentPropsWithoutRef<"nav"> & {
    separator?: React.ReactNode
  }
>(({ ...props }, ref) => <nav ref={ref} aria-label="breadcrumb" {...props} />)
Breadcrumb.displayName = "Breadcrumb"

const BreadcrumbList = React.forwardRef<
  HTMLOListElement,
  React.ComponentPropsWithoutRef<"ol">
>(({ className, ...props }, ref) => (
  <ol
    ref={ref}
    className={cn(
      "flex flex-wrap items-center gap-1.5 break-words text-sm text-muted-foreground sm:gap-2.5",
      className
    )}
    {...props}
  />
))
BreadcrumbList.displayName = "BreadcrumbList"

const BreadcrumbItem = React.forwardRef<
  HTMLLIElement,
  React.ComponentPropsWithoutRef<"li">
>(({ className, ...props }, ref) => (
  <li
    ref={ref}
    className={cn("inline-flex items-center gap-1.5", className)}
    {...props}
  />
))
BreadcrumbItem.displayName = "BreadcrumbItem"

const BreadcrumbLink = React.forwardRef<
  HTMLAnchorElement,
  React.ComponentPropsWithoutRef<"a"> & {
    asChild?: boolean
  }
>(({ asChild, className, ...props }, ref) => {
  const Comp = asChild ? Slot : "a"

  return (
    <Comp
      ref={ref}
      className={cn("transition-colors hover:text-foreground", className)}
      {...props}
    />
  )
})
BreadcrumbLink.displayName = "BreadcrumbLink"

const BreadcrumbPage = React.forwardRef<
  HTMLSpanElement,
  React.ComponentPropsWithoutRef<"span">
>(({ className, ...props }, ref) => (
  <span
    ref={ref}
    role="link"
    aria-disabled="true"
    aria-current="page"
    className={cn("font-normal text-foreground", className)}
    {...props}
  />
))
BreadcrumbPage.displayName = "BreadcrumbPage"

const BreadcrumbSeparator = ({
  children,
  className,
  ...props
}: React.ComponentProps<"li">) => (
  <li
    role="presentation"
    aria-hidden="true"
    className={cn("[&>svg]:w-3.5 [&>svg]:h-3.5", className)}
    {...props}
  >
    {children ?? <ChevronRight />}
  </li>
)
BreadcrumbSeparator.displayName = "BreadcrumbSeparator"

const BreadcrumbEllipsis = ({
  className,
  ...props
}: React.ComponentProps<"span">) => (
  <span
    role="presentation"
    aria-hidden="true"
    className={cn("flex h-9 w-9 items-center justify-center", className)}
    {...props}
  >
    <MoreHorizontal className="h-4 w-4" />
    <span className="sr-only">More</span>
  </span>
)
BreadcrumbEllipsis.displayName = "BreadcrumbElipssis"

export {
  Breadcrumb,
  BreadcrumbList,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbPage,
  BreadcrumbSeparator,
  BreadcrumbEllipsis,
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

app/registro/page.tsx.content:
"use client";

import { useState, useRef, useCallback, useEffect } from "react";
import { useRouter } from "next/navigation";
import { getSupabase } from "@/lib/supabaseClient";

type Step = "signup" | "welcome" | "doc_type" | "camera" | "capture" | "selfie" | "done";

const DOC_TYPES = [
  { value: "cedula", label: "Cedula de Identidad" },
  { value: "pasaporte", label: "Pasaporte" },
  { value: "licencia", label: "Licencia de conducir" },
];

export default function RegistroPage() {
  const router = useRouter();
  const [step, setStep] = useState<Step>("signup");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [categoria, setCategoria] = useState("");
  const [docType, setDocType] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState<{ type: "success" | "error"; text: string } | null>(null);
  const [userId, setUserId] = useState<string | null>(null);
  const [cameraStream, setCameraStream] = useState<MediaStream | null>(null);
  const [cameraError, setCameraError] = useState<string | null>(null);
  const [docFrenteUrl, setDocFrenteUrl] = useState<string | null>(null);
  const [docDorsoUrl, setDocDorsoUrl] = useState<string | null>(null);
  const [selfieUrl, setSelfieUrl] = useState<string | null>(null);
  const [captureStep, setCaptureStep] = useState<"frente" | "dorso">("frente");
  const [uploading, setUploading] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const currentStepNum = (() => {
    if (step === "signup") return 0;
    if (step === "welcome") return 0;
    if (step === "doc_type") return 1;
    if (step === "camera" || step === "capture") return 2;
    if (step === "selfie") return 3;
    return 3;
  })();

  const stopCamera = useCallback(() => {
    if (cameraStream) {
      cameraStream.getTracks().forEach((t) => t.stop());
      setCameraStream(null);
    }
  }, [cameraStream]);

  useEffect(() => {
    return () => {
      if (cameraStream) { cameraStream.getTracks().forEach((t) => t.stop()); }
    };
  }, [cameraStream]);

  async function startCamera() {
    setCameraError(null);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: "environment", width: { ideal: 1280 }, height: { ideal: 720 } },
        audio: false,
      });
      setCameraStream(stream);
      if (videoRef.current) { videoRef.current.srcObject = stream; }
      return true;
    } catch {
      setCameraError("No se pudo acceder a la camara. Verifica los permisos en tu navegador.");
      return false;
    }
  }

  async function startFrontCamera() {
    setCameraError(null);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: "user", width: { ideal: 720 }, height: { ideal: 720 } },
        audio: false,
      });
      setCameraStream(stream);
      if (videoRef.current) { videoRef.current.srcObject = stream; }
      return true;
    } catch {
      setCameraError("No se pudo acceder a la camara frontal.");
      return false;
    }
  }

  function capturePhoto(): Blob | null {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return null;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    if (!ctx) return null;
    ctx.drawImage(video, 0, 0);
    const dataUrl = canvas.toDataURL("image/jpeg", 0.85);
    const byteString = atob(dataUrl.split(",")[1]);
    const ab = new ArrayBuffer(byteString.length);
    const ia = new Uint8Array(ab);
    for (let i = 0; i < byteString.length; i++) { ia[i] = byteString.charCodeAt(i); }
    return new Blob([ab], { type: "image/jpeg" });
  }

  async function uploadToStorage(blob: Blob, path: string): Promise<string | null> {
    const supabase = getSupabase();
    if (!supabase) return null;
    const { error } = await supabase.storage.from("verificaciones").upload(path, blob, { contentType: "image/jpeg", upsert: true });
    if (error) { setMessage({ type: "error", text: `Error al subir imagen: ${error.message}` }); return null; }
    const { data: urlData } = supabase.storage.from("verificaciones").getPublicUrl(path);
    return urlData?.publicUrl || path;
  }

  async function handleSignup(e: React.FormEvent) {
    e.preventDefault();
    setMessage(null);
    if (!email.trim() || !password || !confirmPassword || !categoria) { setMessage({ type: "error", text: "Completa todos los campos." }); return; }
    if (password !== confirmPassword) { setMessage({ type: "error", text: "Las contrasenas no coinciden." }); return; }
    if (password.length < 6) { setMessage({ type: "error", text: "La contrasena debe tener al menos 6 caracteres." }); return; }

    const supabase = getSupabase();
    if (!supabase) { setMessage({ type: "error", text: "Supabase no configurado." }); return; }

    setLoading(true);
    const { data, error } = await supabase.auth.signUp({ email: email.trim(), password });
    if (error) { setMessage({ type: "error", text: error.message }); setLoading(false); return; }

    const uid = data.user?.id;
    if (uid) {
      setUserId(uid);
      await supabase.from("profiles").upsert({ id: uid, email: email.trim(), verificacion_estado: "pendiente", categoria }, { onConflict: "id" });
    }
    setLoading(false);
    setStep("welcome");
  }

  async function handleCameraPermission() {
    setLoading(true);
    const ok = await startCamera();
    setLoading(false);
    if (ok) { setCaptureStep("frente"); setStep("capture"); }
  }

  async function handleCaptureDoc() {
    const blob = capturePhoto();
    if (!blob || !userId) return;
    setUploading(true);
    const side = captureStep === "frente" ? "frente" : "dorso";
    const path = `${userId}/doc_${side}_${Date.now()}.jpg`;
    const url = await uploadToStorage(blob, path);
    if (url) {
      const supabase = getSupabase();
      if (supabase) {
        if (side === "frente") {
          setDocFrenteUrl(url);
          await supabase.from("profiles").update({ doc_frente_url: url }).eq("id", userId);
          setCaptureStep("dorso");
        } else {
          setDocDorsoUrl(url);
          await supabase.from("profiles").update({ doc_dorso_url: url }).eq("id", userId);
          stopCamera();
          setStep("selfie");
        }
      }
    }
    setUploading(false);
  }

  async function handleStartSelfie() {
    setLoading(true);
    stopCamera();
    await new Promise((r) => setTimeout(r, 300));
    const ok = await startFrontCamera();
    setLoading(false);
    if (!ok) return;
  }

  async function handleCaptureSelfie() {
    const blob = capturePhoto();
    if (!blob || !userId) return;
    setUploading(true);
    const path = `${userId}/selfie_${Date.now()}.jpg`;
    const url = await uploadToStorage(blob, path);
    if (url) {
      setSelfieUrl(url);
      const supabase = getSupabase();
      if (supabase) {
        await supabase.from("profiles").update({
          selfie_url: url,
          verificacion_estado: "en_revision",
          fecha_verificacion: new Date().toISOString(),
        }).eq("id", userId);
      }
      stopCamera();
      setStep("done");
    }
    setUploading(false);
  }

  useEffect(() => {
    if (step === "selfie" && !cameraStream) { handleStartSelfie(); }
  }, [step]);

  useEffect(() => {
    if (videoRef.current && cameraStream) { videoRef.current.srcObject = cameraStream; }
  }, [cameraStream, step]);

  return (
    <main className="vv-form-page">
      <div className="vv-form-container">

        {step !== "signup" && step !== "welcome" && step !== "done" && (
          <div className="vv-progress">
            <div className="vv-progress-bar">
              <div className="vv-progress-fill" style={{ width: `${(currentStepNum / 3) * 100}%` }} />
            </div>
            <span className="vv-progress-text">Paso {currentStepNum}/3</span>
          </div>
        )}

        {message && (
          <div className={message.type === "success" ? "vv-form-success-box" : "vv-form-error-box"}>{message.text}</div>
        )}

        {step === "signup" && (
          <>
            <div className="vv-form-header">
              <h1 className="vv-form-title">Crear cuenta</h1>
              <p className="vv-form-subtitle">Registrate para publicar tu perfil.</p>
            </div>
            <form onSubmit={handleSignup} className="vv-form">
              <div className="vv-field">
                <label htmlFor="email" className="vv-label">Email</label>
                <input id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} className="vv-input" placeholder="tu@email.com" required data-testid="input-email" />
              </div>
              <div className="vv-field">
                <label htmlFor="password" className="vv-label">Contrasena</label>
                <input id="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="vv-input" placeholder="Minimo 6 caracteres" required data-testid="input-password" />
              </div>
              <div className="vv-field">
                <label htmlFor="confirmPassword" className="vv-label">Confirmar contrasena</label>
                <input id="confirmPassword" type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} className="vv-input" placeholder="Repeti tu contrasena" required data-testid="input-confirm-password" />
              </div>
              <div className="vv-field">
                <label htmlFor="categoria" className="vv-label">Categoria *</label>
                <select id="categoria" value={categoria} onChange={(e) => setCategoria(e.target.value)} className="vv-input" required data-testid="select-categoria">
                  <option value="">Seleccionar...</option>
                  <option value="mujer">Mujer</option>
                  <option value="hombre">Hombre</option>
                  <option value="trans">Trans</option>
                </select>
              </div>
              <button type="submit" className="vv-btn" disabled={loading} data-testid="button-signup">
                {loading ? "Creando cuenta..." : "Crear cuenta"}
              </button>
            </form>
            <div className="vv-auth-links">
              <a href="/login" className="vv-text-btn" data-testid="link-login">Ya tengo cuenta</a>
            </div>
          </>
        )}

        {step === "welcome" && (
          <div className="vv-welcome">
            <div className="vv-welcome-icon">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" style={{ width: 48, height: 48, color: "#22c55e" }}>
                <path d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
            </div>
            <h1 className="vv-form-title" style={{ textAlign: "center" }}>Verificacion de identidad</h1>
            <p className="vv-welcome-text">
              Necesitamos verificar tu identidad y comprobar que sos mayor de edad.
              Este proceso es obligatorio para poder publicar.
            </p>
            <div className="vv-welcome-steps">
              <div className="vv-welcome-step"><span className="vv-welcome-step-num">1</span><span>Selecciona tu tipo de documento</span></div>
              <div className="vv-welcome-step"><span className="vv-welcome-step-num">2</span><span>Captura frente y dorso del documento</span></div>
              <div className="vv-welcome-step"><span className="vv-welcome-step-num">3</span><span>Toma una selfie para confirmar tu identidad</span></div>
            </div>
            <p className="vv-welcome-privacy">Tus datos se tratan de forma confidencial y solo se usan para verificacion.</p>
            <button className="vv-btn vv-btn-verify" style={{ width: "100%" }} onClick={() => setStep("doc_type")} data-testid="button-start-verification">
              Empezar verificacion
            </button>
          </div>
        )}

        {step === "doc_type" && (
          <>
            <div className="vv-form-header">
              <h1 className="vv-form-title">Tipo de documento</h1>
              <p className="vv-form-subtitle">Selecciona el documento que vas a presentar.</p>
            </div>
            <div className="vv-doc-options">
              {DOC_TYPES.map((dt) => (
                <button
                  key={dt.value}
                  className={`vv-doc-option ${docType === dt.value ? "vv-doc-option-selected" : ""}`}
                  onClick={async () => {
                    setDocType(dt.value);
                    const supabase = getSupabase();
                    if (supabase && userId) { await supabase.from("profiles").update({ tipo_documento: dt.value }).eq("id", userId); }
                  }}
                  data-testid={`button-doc-${dt.value}`}
                >
                  <span className="vv-doc-option-radio">{docType === dt.value ? "\u25CF" : "\u25CB"}</span>
                  {dt.label}
                </button>
              ))}
            </div>
            <button className="vv-btn vv-btn-verify" style={{ width: "100%", marginTop: 16 }} disabled={!docType} onClick={() => setStep("camera")} data-testid="button-continue-doc">
              Continuar
            </button>
          </>
        )}

        {step === "camera" && (
          <>
            <div className="vv-form-header">
              <h1 className="vv-form-title">Permiso de camara</h1>
              <p className="vv-form-subtitle">Necesitamos acceder a tu camara para capturar las fotos del documento.</p>
            </div>
            {cameraError && <div className="vv-form-error-box">{cameraError}</div>}
            <button className="vv-btn vv-btn-verify" style={{ width: "100%" }} disabled={loading} onClick={handleCameraPermission} data-testid="button-allow-camera">
              {loading ? "Solicitando permiso..." : "Permitir camara"}
            </button>
          </>
        )}

        {step === "capture" && (
          <>
            <div className="vv-form-header">
              <h1 className="vv-form-title">{captureStep === "frente" ? "Frente del documento" : "Dorso del documento"}</h1>
              <p className="vv-form-subtitle">{captureStep === "frente" ? "Posiciona el frente de tu documento dentro del recuadro." : "Ahora da vuelta el documento y captura el dorso."}</p>
            </div>
            <div className="vv-camera-wrapper">
              <video ref={videoRef} autoPlay playsInline muted className="vv-camera-video" />
              <canvas ref={canvasRef} style={{ display: "none" }} />
            </div>
            {captureStep === "frente" && docFrenteUrl && <p className="vv-form-subtitle" style={{ color: "#4ade80", marginTop: 8 }}>Frente capturado</p>}
            <button className="vv-btn vv-btn-verify" style={{ width: "100%", marginTop: 12 }} disabled={uploading} onClick={handleCaptureDoc} data-testid="button-capture-doc">
              {uploading ? "Subiendo..." : `Capturar ${captureStep}`}
            </button>
          </>
        )}

        {step === "selfie" && (
          <>
            <div className="vv-form-header">
              <h1 className="vv-form-title">Selfie</h1>
              <p className="vv-form-subtitle">Toma una selfie clara mirando a la camara.</p>
            </div>
            {cameraError && <div className="vv-form-error-box">{cameraError}</div>}
            <div className="vv-camera-wrapper vv-camera-selfie">
              <video ref={videoRef} autoPlay playsInline muted className="vv-camera-video" />
              <canvas ref={canvasRef} style={{ display: "none" }} />
            </div>
            <button className="vv-btn vv-btn-verify" style={{ width: "100%", marginTop: 12 }} disabled={uploading || loading} onClick={handleCaptureSelfie} data-testid="button-capture-selfie">
              {uploading ? "Subiendo..." : loading ? "Iniciando camara..." : "Capturar selfie"}
            </button>
          </>
        )}

        {step === "done" && (
          <div className="vv-welcome">
            <div className="vv-welcome-icon">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" style={{ width: 48, height: 48, color: "#22c55e" }}>
                <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
            </div>
            <h1 className="vv-form-title" style={{ textAlign: "center" }}>Verificacion enviada</h1>
            <p className="vv-welcome-text">
              Tu documentacion fue enviada correctamente. Nuestro equipo la revisara
              y te notificaremos cuando tu cuenta este aprobada.
            </p>
            <p className="vv-welcome-privacy">Este proceso puede tomar entre minutos y 24 horas.</p>
            <button className="vv-btn" style={{ width: "100%" }} onClick={() => router.push("/mi-cuenta")} data-testid="button-go-account">
              Ir a Mi Cuenta
            </button>
          </div>
        )}

        <a href="/" className="vv-form-back" style={{ display: "block", marginTop: 24 }}>Volver al inicio</a>
      </div>
    </main>
  );
}

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);
  --opaque-button-border-intensity: -8;
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);
  --background: 0 0% 100%;
  --foreground: 0 0% 9%;
  --border: 0 0% 91%;
  --card: 0 0% 98%;
  --card-foreground: 0 0% 9%;
  --card-border: 0 0% 94%;
  --sidebar: 0 0% 96%;
  --sidebar-foreground: 0 0% 9%;
  --sidebar-border: 0 0% 92%;
  --sidebar-primary: 340 82% 52%;
  --sidebar-primary-foreground: 0 0% 98%;
  --sidebar-accent: 0 0% 92%;
  --sidebar-accent-foreground: 0 0% 9%;
  --sidebar-ring: 340 82% 52%;
  --popover: 0 0% 95%;
  --popover-foreground: 0 0% 9%;
  --popover-border: 0 0% 90%;
  --primary: 340 82% 52%;
  --primary-foreground: 0 0% 98%;
  --secondary: 0 0% 93%;
  --secondary-foreground: 0 0% 9%;
  --muted: 0 0% 94%;
  --muted-foreground: 0 0% 40%;
  --accent: 340 8% 94%;
  --accent-foreground: 0 0% 9%;
  --destructive: 0 84% 45%;
  --destructive-foreground: 0 0% 98%;
  --input: 0 0% 80%;
  --ring: 340 82% 52%;
  --chart-1: 340 70% 45%;
  --chart-2: 280 65% 48%;
  --chart-3: 200 70% 45%;
  --chart-4: 35 80% 50%;
  --chart-5: 160 60% 42%;
  --font-sans: Open Sans, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 1px 2px -1px hsl(0 0% 0% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 1px 2px -1px hsl(0 0% 0% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 2px 4px -1px hsl(0 0% 0% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 4px 6px -1px hsl(0 0% 0% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 8px 10px -1px hsl(0 0% 0% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

