app/components/PerfilView.tsx.content:
"use client";

import { useEffect, useState, useRef, useCallback } from "react";
import Link from "next/link";
import { useParams } from "next/navigation";
import { getSupabase } from "@/lib/supabaseClient";
import { getPlanConfig } from "@/lib/plans";

interface Publicacion {
  id: string;
  nombre?: string;
  edad?: number;
  zona?: string;
  ciudad?: string;
  departamento?: string;
  cover_url?: string;
  disponible?: boolean;
  rating?: number;
  descripcion?: string;
  user_id?: string;
  categoria?: string;
  altura_cm?: number;
  peso_kg?: number;
  atiende_en?: string | string[];
  horarios?: string;
  tarifa_hora?: number;
  acepta_usd?: boolean;
  telefono?: string;
  servicios?: string[];
  sexo_oral?: string | string[];
  fantasias?: string[];
  servicios_virtuales?: string[];
  tipos_masajes?: string[];
  idiomas?: string[];
}

interface Comment {
  id: string;
  text: string;
  created_at: string;
  user_id?: string;
}

type TabId = "principal" | "info" | "opiniones";

interface ServiceGroup {
  title: string;
  icon: string;
  items: string[];
}

interface PerfilViewProps {
  category: "mujeres" | "hombres" | "trans";
}

const CATEGORY_DB_MAP: Record<string, string> = {
  mujeres: "mujer",
  hombres: "hombre",
  trans: "trans",
};

const REPORT_REASONS = [
  { value: "spam", label: "Spam" },
  { value: "contenido_falso", label: "Contenido falso" },
  { value: "menor", label: "Menor de edad" },
  { value: "otro", label: "Otro" },
];

function normalizePhone(phone: string): string {
  return phone.replace(/[\s\-().]/g, "");
}

function getWhatsAppUrl(phone: string, nombre?: string): string {
  const clean = normalizePhone(phone);
  const num = clean.replace(/^\+/, "");
  const msg = encodeURIComponent(nombre ? `Hola ${nombre}, te vi en VIAVIP` : "Hola, te vi en VIAVIP");
  return `https://wa.me/${num}?text=${msg}`;
}

function getTelUrl(phone: string): string {
  return `tel:${normalizePhone(phone)}`;
}

function getFavKey(id: string): string {
  return `vv_fav_${id}`;
}

export default function PerfilView({ category }: PerfilViewProps) {
  const params = useParams();
  const id = params?.id as string;
  const [pub, setPub] = useState<Publicacion | null>(null);
  const [planId, setPlanId] = useState("free");
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<TabId>("principal");
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [isFav, setIsFav] = useState(false);
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxImg, setLightboxImg] = useState("");
  const [reportOpen, setReportOpen] = useState(false);
  const [reportReason, setReportReason] = useState("spam");
  const [reportDetails, setReportDetails] = useState("");
  const [reportSending, setReportSending] = useState(false);
  const [reportSent, setReportSent] = useState(false);
  const [comments, setComments] = useState<Comment[]>([]);
  const [commentText, setCommentText] = useState("");
  const [commentSending, setCommentSending] = useState(false);
  const [currentUserId, setCurrentUserId] = useState<string | null>(null);

  const principalRef = useRef<HTMLDivElement>(null);
  const infoRef = useRef<HTMLDivElement>(null);
  const opinionesRef = useRef<HTMLDivElement>(null);
  const tabsRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (id) {
      setIsFav(localStorage.getItem(getFavKey(id)) === "1");
    }
  }, [id]);

  useEffect(() => {
    async function load() {
      const supabase = getSupabase();
      if (!supabase || !id) {
        setLoading(false);
        return;
      }

      const { data: userData } = await supabase.auth.getUser();
      if (userData?.user) setCurrentUserId(userData.user.id);

      const { data } = await supabase
        .from("publicaciones")
        .select("*")
        .eq("id", id)
        .maybeSingle();
      if (data) {
        const expectedCat = CATEGORY_DB_MAP[category];
        if (data.categoria && data.categoria !== expectedCat) {
          setPub(null);
          setLoading(false);
          return;
        }
        setPub(data as Publicacion);
        if (data.user_id) {
          const { data: profile } = await supabase
            .from("profiles")
            .select("plan_actual, plan_estado")
            .eq("id", data.user_id)
            .maybeSingle();
          if (profile?.plan_estado === "activo")
            setPlanId(profile.plan_actual || "free");
        }
      }
      setLoading(false);
    }
    load();
  }, [id, category]);

  useEffect(() => {
    async function loadComments() {
      const supabase = getSupabase();
      if (!supabase || !id) return;
      const { data } = await supabase
        .from("comments")
        .select("*")
        .eq("publicacion_id", id)
        .order("created_at", { ascending: false })
        .limit(50);
      if (data) setComments(data);
    }
    if (id) loadComments();
  }, [id]);

  const handleScroll = useCallback(() => {
    setShowScrollTop(window.scrollY > 400);
    const tabsEl = tabsRef.current;
    if (!tabsEl) return;
    const tabsBottom = tabsEl.getBoundingClientRect().bottom;
    const offset = tabsBottom + 20;
    const sections = [
      { id: "opiniones" as TabId, ref: opinionesRef },
      { id: "info" as TabId, ref: infoRef },
      { id: "principal" as TabId, ref: principalRef },
    ];
    for (const section of sections) {
      if (section.ref.current) {
        const rect = section.ref.current.getBoundingClientRect();
        if (rect.top <= offset) {
          setActiveTab(section.id);
          return;
        }
      }
    }
    setActiveTab("principal");
  }, []);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll, { passive: true });
    return () => window.removeEventListener("scroll", handleScroll);
  }, [handleScroll]);

  function scrollToSection(tab: TabId) {
    setActiveTab(tab);
    const refs: Record<TabId, React.RefObject<HTMLDivElement | null>> = {
      principal: principalRef,
      info: infoRef,
      opiniones: opinionesRef,
    };
    const el = refs[tab]?.current;
    if (el) {
      const tabsHeight = tabsRef.current?.offsetHeight || 44;
      const headerHeight = 57;
      const y =
        el.getBoundingClientRect().top +
        window.scrollY -
        headerHeight -
        tabsHeight -
        4;
      window.scrollTo({ top: y, behavior: "smooth" });
    }
  }

  function scrollToTop() {
    window.scrollTo({ top: 0, behavior: "smooth" });
  }

  function toggleFav() {
    if (!id) return;
    const next = !isFav;
    setIsFav(next);
    if (next) {
      localStorage.setItem(getFavKey(id), "1");
    } else {
      localStorage.removeItem(getFavKey(id));
    }
  }

  function openLightbox(src: string) {
    setLightboxImg(src);
    setLightboxOpen(true);
    document.body.style.overflow = "hidden";
  }

  function closeLightbox() {
    setLightboxOpen(false);
    document.body.style.overflow = "";
  }

  async function handleReport() {
    if (reportSending) return;
    setReportSending(true);
    const supabase = getSupabase();
    if (supabase && id) {
      const { error } = await supabase.from("reports").insert({
        publicacion_id: id,
        reason: reportReason,
        details: reportDetails.trim() || null,
        reporter_user_id: currentUserId || null,
      });
      if (error) {
        setReportSending(false);
        return;
      }
    }
    setReportSending(false);
    setReportSent(true);
    setTimeout(() => {
      setReportOpen(false);
      setReportSent(false);
      setReportReason("spam");
      setReportDetails("");
    }, 1500);
  }

  async function handleComment() {
    if (commentSending || !commentText.trim()) return;
    setCommentSending(true);
    const supabase = getSupabase();
    if (supabase && id) {
      const { data, error } = await supabase
        .from("comments")
        .insert({
          publicacion_id: id,
          user_id: currentUserId || null,
          text: commentText.trim(),
        })
        .select("*")
        .single();
      if (!error && data) {
        setComments((prev) => [data, ...prev]);
        setCommentText("");
      }
    }
    setCommentSending(false);
  }

  function buildServiceGroups(p: Publicacion): ServiceGroup[] {
    const groups: ServiceGroup[] = [];
    const servicios = normalizeArray(p.servicios);
    const sexoOral = normalizeArray(p.sexo_oral);
    const fantasias = normalizeArray(p.fantasias);
    const virtuales = normalizeArray(p.servicios_virtuales);
    const masajes = normalizeArray(p.tipos_masajes);
    const idiomas = normalizeArray(p.idiomas);
    if (servicios.length)
      groups.push({ title: "Servicios", icon: "star", items: servicios });
    if (sexoOral.length)
      groups.push({ title: "Sexo Oral", icon: "lips", items: sexoOral });
    if (fantasias.length)
      groups.push({ title: "Fantasias", icon: "sparkle", items: fantasias });
    if (virtuales.length)
      groups.push({ title: "Servicios Virtuales", icon: "video", items: virtuales });
    if (masajes.length)
      groups.push({ title: "Masajes", icon: "star", items: masajes });
    if (idiomas.length)
      groups.push({ title: "Idiomas", icon: "users", items: idiomas });
    return groups;
  }

  if (loading)
    return (
      <main className="vvp-page">
        <div className="vvp-container">
          <p className="vv-form-loading">Cargando...</p>
        </div>
      </main>
    );
  if (!pub)
    return (
      <main className="vvp-page">
        <div className="vvp-container">
          <div className="vv-empty">Perfil no encontrado.</div>
          <Link href={`/${category}`} className="vv-form-back" style={{ display: "block", textAlign: "center" }}>
            Volver
          </Link>
        </div>
      </main>
    );

  const nombre = pub.nombre || "Sin nombre";
  const edad = pub.edad || null;
  const zona = pub.zona || pub.ciudad || "";
  const departamento = pub.departamento || "";
  const disponible = pub.disponible !== false;
  const rating = pub.rating != null ? pub.rating : 4.8;
  const planConfig = getPlanConfig(planId);
  const coverImg = pub.cover_url || "";
  const serviceGroups = buildServiceGroups(pub);
  const hasPhone = !!pub.telefono && pub.telefono.trim().length > 3;
  const atiende = normalizeArray(pub.atiende_en);

  return (
    <main className="vvp-page">
      <div className="vvp-container">
        <Link href={`/${category}`} className="vvp-back-btn" data-testid="link-back">
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M19 12H5M12 19l-7-7 7-7" />
          </svg>
        </Link>

        <button className="vvp-fav-btn" onClick={toggleFav} data-testid="button-favorito" aria-label="Favorito">
          <svg viewBox="0 0 24 24" fill={isFav ? "#e11d48" : "none"} stroke={isFav ? "#e11d48" : "#fff"} strokeWidth="2">
            <path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z" />
          </svg>
        </button>

        <div className="vvp-cover" onClick={() => coverImg && openLightbox(coverImg)} data-testid="image-cover">
          {coverImg ? (
            <img src={coverImg} alt={nombre} className="vvp-cover-img" />
          ) : (
            <div className="vvp-cover-placeholder">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5">
                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" />
                <circle cx="12" cy="7" r="4" />
              </svg>
            </div>
          )}
          <div className="vvp-cover-gradient" />
          <div className="vvp-cover-info">
            <div className="vvp-cover-name-row">
              <h1 className="vvp-cover-name" data-testid="text-nombre">{nombre}</h1>
              {edad && <span className="vvp-cover-age" data-testid="text-age">{edad}</span>}
            </div>
            <div className="vvp-cover-meta">
              {zona && (
                <span className="vvp-cover-loc" data-testid="text-location">
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M12 21s-7-5.3-7-10a7 7 0 1114 0c0 4.7-7 10-7 10z" />
                    <circle cx="12" cy="11" r="2" />
                  </svg>
                  {zona}{departamento ? `, ${departamento}` : ""}
                </span>
              )}
              <span className="vvp-cover-rating" data-testid="text-rating">
                <svg viewBox="0 0 24 24"><path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" /></svg>
                {rating.toFixed(1)}
              </span>
              {disponible && (
                <span className="vvp-cover-online" data-testid="status-disponible">
                  <span className="vvp-cover-online-dot" />
                  Disponible
                </span>
              )}
            </div>
            {planId !== "free" && (
              <span className="vvp-cover-plan" style={{ background: planConfig.badge_bg, color: planConfig.badge_text }} data-testid="badge-plan">
                {planConfig.name}
              </span>
            )}
          </div>
        </div>

        <div className="vvp-actions">
          {hasPhone ? (
            <a href={getWhatsAppUrl(pub.telefono!, nombre)} target="_blank" rel="noopener noreferrer" className="vvp-act-btn vvp-act-wa" data-testid="button-whatsapp">
              <svg viewBox="0 0 24 24" fill="currentColor"><path d="M17.472 14.382c-.297-.149-1.758-.867-2.03-.967-.273-.099-.471-.148-.67.15-.197.297-.767.966-.94 1.164-.173.199-.347.223-.644.075-.297-.15-1.255-.463-2.39-1.475-.883-.788-1.48-1.761-1.653-2.059-.173-.297-.018-.458.13-.606.134-.133.298-.347.446-.52.149-.174.198-.298.298-.497.099-.198.05-.371-.025-.52-.075-.149-.669-1.612-.916-2.207-.242-.579-.487-.5-.669-.51-.173-.008-.371-.01-.57-.01-.198 0-.52.074-.792.372-.272.297-1.04 1.016-1.04 2.479 0 1.462 1.065 2.875 1.213 3.074.149.198 2.096 3.2 5.077 4.487.709.306 1.262.489 1.694.625.712.227 1.36.195 1.871.118.571-.085 1.758-.719 2.006-1.413.248-.694.248-1.289.173-1.413-.074-.124-.272-.198-.57-.347z"/></svg>
              WhatsApp
            </a>
          ) : (
            <button className="vvp-act-btn vvp-act-wa vvp-act-disabled" disabled data-testid="button-whatsapp">
              <svg viewBox="0 0 24 24" fill="currentColor"><path d="M17.472 14.382c-.297-.149-1.758-.867-2.03-.967-.273-.099-.471-.148-.67.15-.197.297-.767.966-.94 1.164-.173.199-.347.223-.644.075-.297-.15-1.255-.463-2.39-1.475-.883-.788-1.48-1.761-1.653-2.059-.173-.297-.018-.458.13-.606.134-.133.298-.347.446-.52.149-.174.198-.298.298-.497.099-.198.05-.371-.025-.52-.075-.149-.669-1.612-.916-2.207-.242-.579-.487-.5-.669-.51-.173-.008-.371-.01-.57-.01-.198 0-.52.074-.792.372-.272.297-1.04 1.016-1.04 2.479 0 1.462 1.065 2.875 1.213 3.074.149.198 2.096 3.2 5.077 4.487.709.306 1.262.489 1.694.625.712.227 1.36.195 1.871.118.571-.085 1.758-.719 2.006-1.413.248-.694.248-1.289.173-1.413-.074-.124-.272-.198-.57-.347z"/></svg>
              WhatsApp
            </button>
          )}
          {hasPhone ? (
            <a href={getTelUrl(pub.telefono!)} className="vvp-act-btn vvp-act-call" data-testid="button-llamar">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72c.127.96.361 1.903.7 2.81a2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45c.907.339 1.85.573 2.81.7A2 2 0 0122 16.92z" />
              </svg>
              Llamar
            </a>
          ) : (
            <button className="vvp-act-btn vvp-act-call vvp-act-disabled" disabled data-testid="button-llamar">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72c.127.96.361 1.903.7 2.81a2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45c.907.339 1.85.573 2.81.7A2 2 0 0122 16.92z" />
              </svg>
              Llamar
            </button>
          )}
        </div>

        <div className="vvp-mini-actions">
          <button className="vvp-mini-act" onClick={() => setReportOpen(true)} data-testid="button-reportar">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M4 15s1-1 4-1 5 2 8 2 4-1 4-1V3s-1 1-4 1-5-2-8-2-4 1-4 1z" />
              <line x1="4" y1="22" x2="4" y2="15" />
            </svg>
            Reportar
          </button>
          <button className="vvp-mini-act" onClick={() => { if (navigator.share) { navigator.share({ title: nombre, url: window.location.href }); } else { navigator.clipboard.writeText(window.location.href); } }} data-testid="button-compartir">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <circle cx="18" cy="5" r="3" /><circle cx="6" cy="12" r="3" /><circle cx="18" cy="19" r="3" />
              <line x1="8.59" y1="13.51" x2="15.42" y2="17.49" /><line x1="15.41" y1="6.51" x2="8.59" y2="10.49" />
            </svg>
            Compartir
          </button>
        </div>

        <div className="vvp-tabs" ref={tabsRef}>
          {(["principal", "info", "opiniones"] as TabId[]).map((tab) => (
            <button
              key={tab}
              className={`vvp-tab ${activeTab === tab ? "vvp-tab-active" : ""}`}
              onClick={() => scrollToSection(tab)}
              data-testid={`tab-${tab}`}
            >
              {tab === "principal" ? "Principal" : tab === "info" ? "Info" : "Opiniones"}
            </button>
          ))}
        </div>

        <div ref={principalRef} className="vvp-section" id="section-principal">
          <h2 className="vvp-section-title">Presentacion</h2>
          <p className="vvp-presentacion">
            {pub.descripcion || "Consulta por WhatsApp para mas informacion."}
          </p>

          {coverImg && (
            <>
              <h2 className="vvp-section-title" style={{ marginTop: 24 }}>Galeria</h2>
              <div className="vvp-gallery-scroll">
                <div className="vvp-gallery-thumb" onClick={() => openLightbox(coverImg)} data-testid="gallery-thumb-0">
                  <img src={coverImg} alt="Foto 1" />
                </div>
              </div>
            </>
          )}
        </div>

        <div ref={infoRef} className="vvp-section" id="section-info">
          <h2 className="vvp-section-title">Informacion</h2>
          <div className="vvp-info-grid">
            {edad && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Edad</span>
                <span className="vvp-info-value">{edad} anos</span>
              </div>
            )}
            {pub.altura_cm && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Altura</span>
                <span className="vvp-info-value">{pub.altura_cm} cm</span>
              </div>
            )}
            {pub.peso_kg && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Peso</span>
                <span className="vvp-info-value">{pub.peso_kg} kg</span>
              </div>
            )}
            {zona && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Zona</span>
                <span className="vvp-info-value">{zona}</span>
              </div>
            )}
            {departamento && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Departamento</span>
                <span className="vvp-info-value">{departamento}</span>
              </div>
            )}
            {atiende.length > 0 && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Atiende en</span>
                <span className="vvp-info-value">{atiende.join(", ")}</span>
              </div>
            )}
            {pub.horarios && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Horarios</span>
                <span className="vvp-info-value">{pub.horarios}</span>
              </div>
            )}
            {pub.tarifa_hora && (
              <div className="vvp-info-item">
                <span className="vvp-info-label">Tarifa/hora</span>
                <span className="vvp-info-value">
                  ${pub.tarifa_hora}
                  {pub.acepta_usd ? " (USD)" : ""}
                </span>
              </div>
            )}
          </div>

          {serviceGroups.length > 0 ? (
            serviceGroups.map((group) => (
              <div key={group.title} className="vvp-service-group">
                <h3 className="vvp-service-group-title">
                  <ServiceIcon icon={group.icon} />
                  {group.title}
                </h3>
                <div className="vvp-chips">
                  {group.items.map((item) => (
                    <span key={item} className="vvp-chip">{item}</span>
                  ))}
                </div>
              </div>
            ))
          ) : (
            <p className="vvp-no-services">
              Consulta por WhatsApp para mas informacion sobre servicios.
            </p>
          )}
        </div>

        <div ref={opinionesRef} className="vvp-section" id="section-opiniones">
          <h2 className="vvp-section-title">Opiniones</h2>

          <div className="vvp-comment-form">
            <textarea
              className="vvp-comment-input"
              placeholder="Escribe tu opinion..."
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              rows={2}
              maxLength={500}
              data-testid="input-comment"
            />
            <button
              className="vvp-comment-submit"
              onClick={handleComment}
              disabled={commentSending || !commentText.trim()}
              data-testid="button-comment-submit"
            >
              {commentSending ? "Enviando..." : "Publicar"}
            </button>
          </div>

          {comments.length > 0 ? (
            <div className="vvp-comments-list">
              {comments.map((c) => (
                <div key={c.id} className="vvp-comment-item" data-testid={`comment-${c.id}`}>
                  <div className="vvp-comment-header">
                    <span className="vvp-comment-avatar">
                      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><circle cx="12" cy="7" r="4" /><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" /></svg>
                    </span>
                    <span className="vvp-comment-date">
                      {new Date(c.created_at).toLocaleDateString("es-UY", { day: "numeric", month: "short", year: "numeric" })}
                    </span>
                  </div>
                  <p className="vvp-comment-text">{c.text}</p>
                </div>
              ))}
            </div>
          ) : (
            <div className="vvp-opiniones-empty">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5">
                <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" />
              </svg>
              <p>Aun no hay opiniones</p>
            </div>
          )}
        </div>

        <div style={{ height: 90 }} />
      </div>

      {hasPhone && (
        <div className="vvp-sticky-wa" data-testid="sticky-whatsapp">
          <a href={getWhatsAppUrl(pub.telefono!, nombre)} target="_blank" rel="noopener noreferrer" className="vvp-sticky-wa-btn">
            <svg viewBox="0 0 24 24" fill="currentColor"><path d="M17.472 14.382c-.297-.149-1.758-.867-2.03-.967-.273-.099-.471-.148-.67.15-.197.297-.767.966-.94 1.164-.173.199-.347.223-.644.075-.297-.15-1.255-.463-2.39-1.475-.883-.788-1.48-1.761-1.653-2.059-.173-.297-.018-.458.13-.606.134-.133.298-.347.446-.52.149-.174.198-.298.298-.497.099-.198.05-.371-.025-.52-.075-.149-.669-1.612-.916-2.207-.242-.579-.487-.5-.669-.51-.173-.008-.371-.01-.57-.01-.198 0-.52.074-.792.372-.272.297-1.04 1.016-1.04 2.479 0 1.462 1.065 2.875 1.213 3.074.149.198 2.096 3.2 5.077 4.487.709.306 1.262.489 1.694.625.712.227 1.36.195 1.871.118.571-.085 1.758-.719 2.006-1.413.248-.694.248-1.289.173-1.413-.074-.124-.272-.198-.57-.347z"/></svg>
            Enviar WhatsApp
          </a>
        </div>
      )}

      {showScrollTop && (
        <button className="vvp-scroll-top" onClick={scrollToTop} data-testid="button-scroll-top">
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
            <path d="M18 15l-6-6-6 6" />
          </svg>
        </button>
      )}

      {lightboxOpen && (
        <div className="vvp-lightbox" onClick={closeLightbox} data-testid="lightbox">
          <button className="vvp-lightbox-close" onClick={closeLightbox}>&times;</button>
          <img src={lightboxImg} alt="Foto" className="vvp-lightbox-img" onClick={(e) => e.stopPropagation()} />
        </div>
      )}

      {reportOpen && (
        <div className="vvp-modal-overlay" onClick={() => !reportSending && setReportOpen(false)}>
          <div className="vvp-modal" onClick={(e) => e.stopPropagation()} data-testid="modal-report">
            <h3 className="vvp-modal-title">Reportar perfil</h3>
            {reportSent ? (
              <div className="vvp-report-sent">
                <svg viewBox="0 0 24 24" fill="none" stroke="#22c55e" strokeWidth="2"><path d="M20 6L9 17l-5-5" /></svg>
                <p>Reporte enviado</p>
              </div>
            ) : (
              <>
                <div className="vv-field">
                  <label className="vv-label">Motivo</label>
                  <select className="vv-input" value={reportReason} onChange={(e) => setReportReason(e.target.value)} data-testid="select-report-reason">
                    {REPORT_REASONS.map((r) => (
                      <option key={r.value} value={r.value}>{r.label}</option>
                    ))}
                  </select>
                </div>
                <div className="vv-field" style={{ marginTop: 12 }}>
                  <label className="vv-label">Detalles (opcional)</label>
                  <textarea className="vv-textarea" rows={3} value={reportDetails} onChange={(e) => setReportDetails(e.target.value)} placeholder="Describe el problema..." data-testid="input-report-details" />
                </div>
                <div style={{ display: "flex", gap: 8, marginTop: 16 }}>
                  <button className="vv-btn-secondary" style={{ flex: 1 }} onClick={() => setReportOpen(false)}>Cancelar</button>
                  <button className="vv-btn" style={{ flex: 1, marginTop: 0 }} onClick={handleReport} disabled={reportSending} data-testid="button-report-submit">
                    {reportSending ? "Enviando..." : "Enviar reporte"}
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </main>
  );
}

function ServiceIcon({ icon }: { icon: string }) {
  if (icon === "users")
    return <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" /><circle cx="9" cy="7" r="4" /><path d="M23 21v-2a4 4 0 0 0-3-3.87" /><path d="M16 3.13a4 4 0 0 1 0 7.75" /></svg>;
  if (icon === "star")
    return <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" /></svg>;
  if (icon === "lips")
    return <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><circle cx="12" cy="12" r="10" /><path d="M8 14s1.5 2 4 2 4-2 4-2" /></svg>;
  if (icon === "sparkle")
    return <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M12 3v1m0 16v1m-7.07-2.93l.7-.7M18.36 5.64l.7-.7M3 12h1m16 0h1M5.64 5.64l.7.7M17.66 17.66l.7.7" /></svg>;
  if (icon === "video")
    return <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><rect x="2" y="7" width="15" height="10" rx="2" /><path d="M17 9l5-3v12l-5-3" /></svg>;
  return null;
}

function normalizeArray(v: unknown): string[] {
  if (v == null) return [];
  if (Array.isArray(v))
    return v.map(String).map((s) => s.trim()).filter(Boolean);
  if (typeof v === "string") {
    const s = v.trim();
    if (!s) return [];
    if (s.startsWith("{") && s.endsWith("}")) {
      const inner = s.slice(1, -1).trim();
      if (!inner) return [];
      const out: string[] = [];
      let cur = "";
      let inQuotes = false;
      for (let i = 0; i < inner.length; i++) {
        const ch = inner[i];
        if (ch === '"') { inQuotes = !inQuotes; continue; }
        if (ch === "," && !inQuotes) {
          const t = cur.trim();
          if (t) out.push(t);
          cur = "";
          continue;
        }
        cur += ch;
      }
      const t = cur.trim();
      if (t) out.push(t);
      return out.map((x) => x.trim()).filter(Boolean);
    }
    return [s];
  }
  return [];
}

