import Link from "next/link";
import { getSupabasePublicClient } from "@/lib/supabasePublic";
import { getPlanConfig } from "@/lib/plans";
import HomeClient from "./components/HomeClient";

interface Publicacion {
  id: string | number;
  nombre?: string;
  edad?: number;
  zona?: string;
  ciudad?: string;
  cover_url?: string;
  disponible?: boolean;
  rating?: number;
  created_at?: string;
  user_id?: string;
  plan_actual?: string;
  categoria?: string;
}

export default async function Home() {
  const supabase = getSupabasePublicClient();
  let items: Publicacion[] = [];
  let destacadas: Publicacion[] = [];

  if (supabase) {
    try {
      const { data: pubs } = await supabase
        .from("publicaciones")
        .select("id, nombre, edad, zona, ciudad, cover_url, disponible, rating, created_at, user_id, categoria")
        .not("cover_url", "is", null)
        .neq("cover_url", "");

      if (pubs && pubs.length > 0) {
        const userIds = pubs.map((p: any) => p.user_id).filter(Boolean);
        let planMap: Record<string, string> = {};

        if (userIds.length > 0) {
          const { data: profiles } = await supabase.from("profiles").select("id, plan_actual, plan_estado").in("id", userIds);
          if (profiles) {
            for (const p of profiles) {
              planMap[p.id] = p.plan_estado === "activo" ? (p.plan_actual || "free") : "free";
            }
          }
        }

        items = (pubs as Publicacion[]).map((p) => ({ ...p, plan_actual: p.user_id ? planMap[p.user_id] || "free" : "free" }));
        items.sort((a, b) => {
          const pa = getPlanConfig(a.plan_actual).ranking_priority;
          const pb = getPlanConfig(b.plan_actual).ranking_priority;
          if (pb !== pa) return pb - pa;
          return (b.rating || 0) - (a.rating || 0);
        });

        destacadas = items.filter((i) => {
          const plan = i.plan_actual || "free";
          return plan === "diamante" || plan === "platino";
        });
      }
    } catch { /* ignore */ }
  }

  const disponibles = items.filter((i) => i.disponible !== false);
  const premium = items.filter((i) => {
    const p = getPlanConfig(i.plan_actual);
    return p.ranking_priority >= 2;
  });

  return <HomeClient items={items} disponibles={disponibles} premium={premium} destacadas={destacadas} />;
}

