app/admin/verificaciones/page.tsx.content:
"use client";

import { useEffect, useState } from "react";
import { getSupabase } from "@/lib/supabaseClient";

interface Profile {
  id: string;
  email?: string;
  verificacion_estado?: string;
  tipo_documento?: string;
  doc_frente_url?: string;
  doc_dorso_url?: string;
  selfie_url?: string;
  fecha_verificacion?: string;
}

export default function AdminVerificacionesPage() {
  const [profiles, setProfiles] = useState<Profile[]>([]);
  const [loading, setLoading] = useState(true);
  const [authError, setAuthError] = useState<string | null>(null);
  const [actionMsg, setActionMsg] = useState<{
    id: string;
    type: "success" | "error";
    text: string;
  } | null>(null);
  const [updating, setUpdating] = useState<string | null>(null);

  useEffect(() => {
    async function load() {
      const supabase = getSupabase();
      if (!supabase) {
        setAuthError("Supabase no configurado.");
        setLoading(false);
        return;
      }

      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) {
        setAuthError("Inicia sesion para acceder.");
        setLoading(false);
        return;
      }

      const { data: adminProfile } = await supabase
        .from("profiles")
        .select("is_admin")
        .eq("id", user.id)
        .maybeSingle();
      if (!adminProfile?.is_admin) {
        setAuthError("No tenes permisos para acceder a esta pagina.");
        setLoading(false);
        return;
      }

      const { data, error } = await supabase
        .from("profiles")
        .select("*")
        .in("verificacion_estado", [
          "en_revision",
          "pendiente",
          "aprobado",
          "rechazado",
        ])
        .order("fecha_verificacion", { ascending: false });
      if (error) {
        setAuthError(`Error: ${error.message}`);
      } else {
        setProfiles((data || []) as Profile[]);
      }
      setLoading(false);
    }
    load();
  }, []);

  async function handleAction(
    profileId: string,
    action: "aprobado" | "rechazado",
  ) {
    setUpdating(profileId);
    setActionMsg(null);
    const supabase = getSupabase();
    if (!supabase) return;

    const { error } = await supabase
      .from("profiles")
      .update({ verificacion_estado: action })
      .eq("id", profileId);
    if (error) {
      setActionMsg({ id: profileId, type: "error", text: error.message });
    } else {
      setActionMsg({
        id: profileId,
        type: "success",
        text: action === "aprobado" ? "Aprobado" : "Rechazado",
      });
      setProfiles((prev) =>
        prev.map((p) =>
          p.id === profileId ? { ...p, verificacion_estado: action } : p,
        ),
      );
    }
    setUpdating(null);
  }

  if (loading)
    return (
      <main className="vv-form-page">
        <div className="vv-admin-container">
          <p className="vv-form-loading">Cargando...</p>
        </div>
      </main>
    );
  if (authError)
    return (
      <main className="vv-form-page">
        <div className="vv-admin-container">
          <div className="vv-form-error-box">{authError}</div>
        </div>
      </main>
    );

  return (
    <main className="vv-form-page">
      <div className="vv-admin-container">
        <div className="vv-form-header">
          <h1 className="vv-form-title">Panel de verificaciones</h1>
          <p className="vv-form-subtitle">{profiles.length} registro(s)</p>
        </div>

        {profiles.length === 0 && (
          <div className="vv-empty">No hay verificaciones pendientes.</div>
        )}

        <div className="vv-admin-list">
          {profiles.map((p) => (
            <div key={p.id} className="vv-admin-card">
              <div className="vv-admin-card-header">
                <span className="vv-admin-email">{p.email || p.id}</span>
                <span
                  className={`vv-admin-status vv-admin-status-${p.verificacion_estado}`}
                >
                  {p.verificacion_estado}
                </span>
              </div>

              {p.tipo_documento && (
                <p className="vv-admin-detail">Documento: {p.tipo_documento}</p>
              )}
              {p.fecha_verificacion && (
                <p className="vv-admin-detail">
                  Fecha: {new Date(p.fecha_verificacion).toLocaleString()}
                </p>
              )}

              <div className="vv-admin-images">
                {p.doc_frente_url && (
                  <div className="vv-admin-img-box">
                    <p className="vv-admin-img-label">Frente</p>
                    <img
                      src={p.doc_frente_url}
                      alt="Frente"
                      className="vv-admin-img"
                    />
                  </div>
                )}
                {p.doc_dorso_url && (
                  <div className="vv-admin-img-box">
                    <p className="vv-admin-img-label">Dorso</p>
                    <img
                      src={p.doc_dorso_url}
                      alt="Dorso"
                      className="vv-admin-img"
                    />
                  </div>
                )}
                {p.selfie_url && (
                  <div className="vv-admin-img-box">
                    <p className="vv-admin-img-label">Selfie</p>
                    <img
                      src={p.selfie_url}
                      alt="Selfie"
                      className="vv-admin-img"
                    />
                  </div>
                )}
              </div>

              {actionMsg && actionMsg.id === p.id && (
                <div
                  className={
                    actionMsg.type === "success"
                      ? "vv-form-success-box"
                      : "vv-form-error-box"
                  }
                  style={{ marginTop: 8 }}
                >
                  {actionMsg.text}
                </div>
              )}

              {(p.verificacion_estado === "en_revision" ||
                p.verificacion_estado === "pendiente") && (
                <div className="vv-admin-actions">
                  <button
                    className="vv-btn vv-btn-approve"
                    disabled={updating === p.id}
                    onClick={() => handleAction(p.id, "aprobado")}
                    data-testid={`button-approve-${p.id}`}
                  >
                    {updating === p.id ? "..." : "Aprobar"}
                  </button>
                  <button
                    className="vv-btn vv-btn-reject"
                    disabled={updating === p.id}
                    onClick={() => handleAction(p.id, "rechazado")}
                    data-testid={`button-reject-${p.id}`}
                  >
                    {updating === p.id ? "..." : "Rechazar"}
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </main>
  );
}

app/reset-password/page.tsx.content:
"use client";

import { useState } from "react";
import { getSupabase } from "@/lib/supabaseClient";

export default function ResetPasswordPage() {
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState<{ type: "success" | "error"; text: string } | null>(null);

  async function handleSubmit(e: React.FormEvent) {
    e.preventDefault();
    setMessage(null);

    if (!password || password.length < 6) {
      setMessage({ type: "error", text: "La contrasena debe tener al menos 6 caracteres." });
      return;
    }

    if (password !== confirm) {
      setMessage({ type: "error", text: "Las contrasenas no coinciden." });
      return;
    }

    const supabase = getSupabase();
    if (!supabase) {
      setMessage({ type: "error", text: "Supabase no configurado." });
      return;
    }

    setLoading(true);

    try {
      const { error } = await supabase.auth.updateUser({ password });
      if (error) {
        setMessage({ type: "error", text: error.message });
      } else {
        setMessage({ type: "success", text: "Contrasena actualizada correctamente." });
        setPassword("");
        setConfirm("");
      }
    } catch (err: unknown) {
      const msg = err instanceof Error ? err.message : "Error desconocido";
      setMessage({ type: "error", text: msg });
    }

    setLoading(false);
  }

  return (
    <main className="vv-form-page">
      <div className="vv-form-container">
        <div className="vv-form-header">
          <h1 className="vv-form-title">Nueva contrasena</h1>
          <p className="vv-form-subtitle">Ingresa tu nueva contrasena.</p>
        </div>

        {message && (
          <div className={message.type === "success" ? "vv-form-success-box" : "vv-form-error-box"}>
            {message.text}
          </div>
        )}

        <form onSubmit={handleSubmit} className="vv-form">
          <div className="vv-field">
            <label htmlFor="password" className="vv-label">Nueva contrasena</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="vv-input"
              placeholder="Minimo 6 caracteres"
              required
              data-testid="input-new-password"
            />
          </div>

          <div className="vv-field">
            <label htmlFor="confirm" className="vv-label">Confirmar contrasena</label>
            <input
              id="confirm"
              type="password"
              value={confirm}
              onChange={(e) => setConfirm(e.target.value)}
              className="vv-input"
              placeholder="Repeti tu contrasena"
              required
              data-testid="input-confirm-password"
            />
          </div>

          <button type="submit" className="vv-btn" disabled={loading} data-testid="button-save-password">
            {loading ? "Guardando..." : "Guardar contrasena"}
          </button>
        </form>

        <a href="/login" className="vv-form-link" style={{ marginTop: "20px", display: "inline-block" }}>
          Ir a Login
        </a>
      </div>
    </main>
  );
}
